import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import Navbar from '../components/Navbar';
import ticketService from '../services/ticketService';
import TicketValidator from '../utils/validation';
import '../styles/CreateTicket.css';

const CreateTicket = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    issueType: 'software',
    urgency: 'medium',
    category: '',
    department: '',
    location: '',
    contactInfo: user?.email || '',
    attachments: []
  });
  
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [validationErrors, setValidationErrors] = useState({});
  const [selectedFiles, setSelectedFiles] = useState([]);
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear validation error for this field
    if (validationErrors[name]) {
      setValidationErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };
  
  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    
    // Validate file size and type
    const validFiles = files.filter(file => {
      if (file.size > 10 * 1024 * 1024) {
        setError(`${file.name} exceeds 10MB size limit`);
        return false;
      }
      
      const allowedTypes = [
        'image/jpeg', 'image/png', 'image/gif',
        'application/pdf',
        'application/msword',
        'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
        'application/vnd.ms-excel',
        'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
        'text/plain', 'text/csv'
      ];
      
      if (!allowedTypes.includes(file.type)) {
        setError(`${file.name} has unsupported file type`);
        return false;
      }
      
      return true;
    });
    
    setSelectedFiles(prev => [...prev, ...validFiles]);
    setFormData(prev => ({
      ...prev,
      attachments: [...prev.attachments, ...validFiles]
    }));
  };
  
  const removeFile = (index) => {
    setSelectedFiles(prev => prev.filter((_, i) => i !== index));
    setFormData(prev => ({
      ...prev,
      attachments: prev.attachments.filter((_, i) => i !== index)
    }));
  };
  
  const validateForm = () => {
    const errors = {};
    
    if (!formData.title.trim()) {
      errors.title = 'Title is required';
    } else if (formData.title.trim().length < 5) {
      errors.title = 'Title must be at least 5 characters';
    }
    
    if (!formData.description.trim()) {
      errors.description = 'Description is required';
    } else if (formData.description.trim().length < 10) {
      errors.description = 'Description must be at least 10 characters';
    }
    
    if (!formData.issueType) {
      errors.issueType = 'Issue type is required';
    }
    
    if (!formData.urgency) {
      errors.urgency = 'Urgency level is required';
    }
    
    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    try {
      setLoading(true);
      setError('');
      
      // Prepare ticket data
      const ticketData = {
        title: formData.title.trim(),
        description: formData.description.trim(),
        issueType: formData.issueType,
        urgency: formData.urgency,
        createdBy: user?.id || user?._id,
        ...(formData.category && { category: formData.category.trim() }),
        ...(formData.department && { department: formData.department.trim() }),
        ...(formData.location && { location: formData.location.trim() }),
        ...(formData.contactInfo && { contactInfo: formData.contactInfo.trim() }),
        status: 'open',
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      };
      
      // Use validation service
      const validationResult = TicketValidator.validateTicketData(ticketData);
      
      if (!validationResult.valid) {
        setError(validationResult.errors.join(', '));
        return;
      }
      
      // Create ticket
      const response = await ticketService.createTicket(validationResult.sanitizedData);
      
      if (response && response.success) {
        const ticketId = response.data?.ticket?._id || response.data?._id || response.ticket?._id;
        
        // Upload attachments if any
        if (selectedFiles.length > 0 && ticketId) {
          for (const file of selectedFiles) {
            try {
              await ticketService.uploadAttachment(ticketId, file);
            } catch (uploadError) {
              console.warn('Failed to upload attachment:', uploadError);
            }
          }
        }
        
        setSuccess(true);
        
        // Redirect to ticket detail after 2 seconds
        setTimeout(() => {
          if (ticketId) {
            navigate(`/tickets/${ticketId}`);
          } else {
            navigate('/tickets');
          }
        }, 2000);
      } else {
        throw new Error(response?.message || 'Failed to create ticket');
      }
    } catch (error) {
      console.error('Error creating ticket:', error);
      setError(error.message || 'An error occurred while creating the ticket. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  
  if (success) {
    return (
      <>
        <Navbar />
        <div className="success-container">
          <div className="success-icon">âœ…</div>
          <h2>Ticket Created Successfully!</h2>
          <p>Your support ticket has been submitted. You will be redirected shortly.</p>
          <p className="redirect-message">Redirecting to ticket details...</p>
        </div>
      </>
    );
  }
  
  return (
    <>
      <Navbar />
      <div className="create-ticket-page">
        <div className="create-ticket-header">
          <h1>Create New Support Ticket</h1>
          <p>Submit a request for IT support. Please provide as much detail as possible.</p>
        </div>
        
        <form onSubmit={handleSubmit} className="ticket-form">
          {error && (
            <div className="error-message">
              <span>{error}</span>
              <button type="button" onClick={() => setError('')}>Ã—</button>
            </div>
          )}
          
          {/* Title Field */}
          <div className="form-group">
            <label htmlFor="title">
              Ticket Title *
              {validationErrors.title && (
                <span className="error-text"> - {validationErrors.title}</span>
              )}
            </label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="Brief description of the issue"
              maxLength="200"
              required
              disabled={loading}
            />
            <div className="character-count">
              {formData.title.length}/200 characters
            </div>
          </div>
          
          {/* Description Field */}
          <div className="form-group">
            <label htmlFor="description">
              Description *
              {validationErrors.description && (
                <span className="error-text"> - {validationErrors.description}</span>
              )}
            </label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="Please provide detailed information about the issue, including steps to reproduce, error messages, and any troubleshooting you've already attempted."
              rows="6"
              maxLength="5000"
              required
              disabled={loading}
            />
            <div className="character-count">
              {formData.description.length}/5000 characters
            </div>
          </div>
          
          <div className="form-row">
            {/* Issue Type */}
            <div className="form-group">
              <label htmlFor="issueType">
                Issue Type *
                {validationErrors.issueType && (
                  <span className="error-text"> - {validationErrors.issueType}</span>
                )}
              </label>
              <select
                id="issueType"
                name="issueType"
                value={formData.issueType}
                onChange={handleChange}
                required
                disabled={loading}
              >
                <option value="hardware">Hardware</option>
                <option value="software">Software</option>
                <option value="network">Network/Internet</option>
                <option value="account">Account/Access</option>
                <option value="security">Security</option>
                <option value="other">Other</option>
              </select>
            </div>
            
            {/* Urgency Level */}
            <div className="form-group">
              <label htmlFor="urgency">
                Urgency Level *
                {validationErrors.urgency && (
                  <span className="error-text"> - {validationErrors.urgency}</span>
                )}
              </label>
              <select
                id="urgency"
                name="urgency"
                value={formData.urgency}
                onChange={handleChange}
                required
                disabled={loading}
              >
                <option value="low">Low (Within 72 hours)</option>
                <option value="medium">Medium (Within 48 hours)</option>
                <option value="high">High (Within 24 hours)</option>
                <option value="critical">Critical (Within 4 hours)</option>
              </select>
              <div className="urgency-help">
                {formData.urgency === 'low' && 'Response expected within 72 business hours'}
                {formData.urgency === 'medium' && 'Response expected within 48 business hours'}
                {formData.urgency === 'high' && 'Response expected within 24 business hours'}
                {formData.urgency === 'critical' && 'Immediate attention required - system down or security breach'}
              </div>
            </div>
          </div>
          
          <div className="form-row">
            {/* Category */}
            <div className="form-group">
              <label htmlFor="category">Category</label>
              <input
                type="text"
                id="category"
                name="category"
                value={formData.category}
                onChange={handleChange}
                placeholder="e.g., Email, WiFi, Software Installation"
                maxLength="100"
                disabled={loading}
              />
            </div>
            
            {/* Department */}
            <div className="form-group">
              <label htmlFor="department">Department</label>
              <input
                type="text"
                id="department"
                name="department"
                value={formData.department}
                onChange={handleChange}
                placeholder="Your department/school"
                maxLength="100"
                disabled={loading}
              />
            </div>
          </div>
          
          <div className="form-row">
            {/* Location */}
            <div className="form-group">
              <label htmlFor="location">Location</label>
              <input
                type="text"
                id="location"
                name="location"
                value={formData.location}
                onChange={handleChange}
                placeholder="Building, Room Number, Lab Name"
                maxLength="200"
                disabled={loading}
              />
            </div>
            
            {/* Contact Info */}
            <div className="form-group">
              <label htmlFor="contactInfo">Contact Information</label>
              <input
                type="text"
                id="contactInfo"
                name="contactInfo"
                value={formData.contactInfo}
                onChange={handleChange}
                placeholder="Phone number or alternative email"
                maxLength="100"
                disabled={loading}
              />
            </div>
          </div>
          
          {/* File Attachments */}
          <div className="form-group">
            <label htmlFor="attachments">Attachments (Optional)</label>
            <div className="file-upload-area">
              <input
                type="file"
                id="attachments"
                multiple
                onChange={handleFileChange}
                accept=".jpg,.jpeg,.png,.gif,.pdf,.doc,.docx,.xls,.xlsx,.txt,.csv"
                disabled={loading || selectedFiles.length >= 10}
              />
              <div className="upload-prompt">
                <div className="upload-icon">ðŸ“Ž</div>
                <p>Click to upload files or drag and drop</p>
                <p className="upload-help">Maximum 10 files, 10MB each. Supported: Images, PDF, Word, Excel, Text</p>
              </div>
            </div>
            
            {selectedFiles.length > 0 && (
              <div className="file-list">
                <h4>Selected Files ({selectedFiles.length}/10)</h4>
                {selectedFiles.map((file, index) => (
                  <div key={index} className="file-item">
                    <span className="file-name">{file.name}</span>
                    <span className="file-size">
                      {(file.size / 1024 / 1024).toFixed(2)} MB
                    </span>
                    <button
                      type="button"
                      onClick={() => removeFile(index)}
                      className="remove-file"
                      disabled={loading}
                    >
                      Ã—
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
          
          {/* Form Actions */}
          <div className="form-actions">
            <button
              type="button"
              onClick={() => navigate('/tickets')}
              className="btn-secondary"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn-primary"
              disabled={loading}
            >
              {loading ? (
                <>
                  <span className="spinner-small"></span>
                  Creating Ticket...
                </>
              ) : (
                'Submit Ticket'
              )}
            </button>
          </div>
          
          {/* Form Help Text */}
          <div className="form-help">
            <p><strong>Tips for faster resolution:</strong></p>
            <ul>
              <li>Be specific about the problem and include error messages if any</li>
              <li>Mention what you've already tried to fix the issue</li>
              <li>Include relevant device information (computer model, OS version)</li>
              <li>Provide screenshots if possible using the attachment feature</li>
              <li>Include your availability for follow-up if needed</li>
            </ul>
          </div>
        </form>
      </div>
    </>
  );
};

export default CreateTicket;
