import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import Navbar from '../components/Navbar';
import '../styles/Auth.css';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'student', // Changed default to student
    department: '',
    studentId: '',
    phone: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const { register } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Enhanced validation
    if (!formData.username || !formData.email || !formData.password) {
      setError('Please fill in all required fields');
      setLoading(false);
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters');
      setLoading(false);
      return;
    }

    // Student-specific validation
    if (formData.role === 'student' && !formData.studentId) {
      setError('Student ID is required for student accounts');
      setLoading(false);
      return;
    }

    try {
      console.log('Registering user:', formData);
      
      // Prepare data for registration
      const registrationData = {
        username: formData.username,
        email: formData.email,
        password: formData.password,
        role: formData.role,
        department: formData.department,
        ...(formData.role === 'student' && { studentId: formData.studentId }),
        ...(formData.phone && { phone: formData.phone })
      };

      await register(registrationData);
      
      // Redirect based on role using our RoleBasedDashboard
      navigate('/dashboard');
      
    } catch (err) {
      console.error('Registration error:', err);
      setError(err.response?.data?.message || err.message || 'Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Role options with descriptions
  const roleOptions = [
    {
      value: 'student',
      label: 'Student',
      description: 'Get help with academic technology and coursework',
      icon: 'üéì'
    },
    {
      value: 'staff',
      label: 'Staff/Faculty', 
      description: 'University staff and teaching faculty members',
      icon: 'üë®‚Äçüè´'
    },
    {
      value: 'technician',
      label: 'IT Technician',
      description: 'Provide technical support and resolve tickets',
      icon: 'üë®‚Äçüíª'
    },
    {
      value: 'admin',
      label: 'Administrator',
      description: 'System administration and user management',
      icon: 'üë®‚Äçüíº'
    }
  ];

  // Departments for selection
  const departments = [
    'Computer Science',
    'Engineering', 
    'Business',
    'Arts & Sciences',
    'Medicine',
    'Law',
    'Education',
    'IT Services',
    'Administration',
    'Other'
  ];

  return (
    <>
      <Navbar />
      <div className="auth-container">
        <div className="auth-form">
          <div className="auth-header">
            <h2>Create Your Account</h2>
            <p className="auth-subtitle">Join the University IT Support System</p>
          </div>
          
          {error && (
            <div className="error-message">
              <span className="error-icon">‚ö†Ô∏è</span>
              {error}
            </div>
          )}
          
          <form onSubmit={handleSubmit} className="register-form">
            {/* Personal Information */}
            <div className="form-section">
              <h3 className="section-title">Personal Information</h3>
              
              <div className="form-group">
                <label htmlFor="username">Full Name *</label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  value={formData.username}
                  onChange={handleChange}
                  required
                  placeholder="Enter your full name"
                  className="form-input"
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="email">University Email *</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  placeholder="name@university.edu"
                  className="form-input"
                />
              </div>

              <div className="form-group">
                <label htmlFor="phone">Phone Number (Optional)</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  placeholder="+1 (555) 123-4567"
                  className="form-input"
                />
              </div>
            </div>

            {/* Account Type */}
            <div className="form-section">
              <h3 className="section-title">Account Type</h3>
              
              <div className="role-selection">
                {roleOptions.map(option => (
                  <label 
                    key={option.value} 
                    className={`role-option ${formData.role === option.value ? 'selected' : ''}`}
                  >
                    <input
                      type="radio"
                      name="role"
                      value={option.value}
                      checked={formData.role === option.value}
                      onChange={handleChange}
                      className="role-radio"
                    />
                    <div className="role-content">
                      <span className="role-icon">{option.icon}</span>
                      <div className="role-info">
                        <span className="role-label">{option.label}</span>
                        <span className="role-description">{option.description}</span>
                      </div>
                    </div>
                  </label>
                ))}
              </div>
            </div>

            {/* Role-Specific Fields */}
            <div className="form-section">
              <h3 className="section-title">Additional Information</h3>
              
              <div className="form-group">
                <label htmlFor="department">Department</label>
                <select
                  id="department"
                  name="department"
                  value={formData.department}
                  onChange={handleChange}
                  className="form-select"
                >
                  <option value="">Select your department</option>
                  {departments.map(dept => (
                    <option key={dept} value={dept}>{dept}</option>
                  ))}
                </select>
              </div>

              {/* Student-specific field */}
              {formData.role === 'student' && (
                <div className="form-group">
                  <label htmlFor="studentId">Student ID *</label>
                  <input
                    type="text"
                    id="studentId"
                    name="studentId"
                    value={formData.studentId}
                    onChange={handleChange}
                    required={formData.role === 'student'}
                    placeholder="Enter your student ID"
                    className="form-input"
                  />
                </div>
              )}

              {/* Technician/Admin note */}
              {(formData.role === 'technician' || formData.role === 'admin') && (
                <div className="info-note">
                  <span className="note-icon">‚ÑπÔ∏è</span>
                  <div className="note-content">
                    <strong>Verification Required</strong>
                    <p>Technician and administrator accounts require manual verification by system administrators.</p>
                  </div>
                </div>
              )}
            </div>

            {/* Security */}
            <div className="form-section">
              <h3 className="section-title">Security</h3>
              
              <div className="form-group">
                <label htmlFor="password">Password *</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  required
                  placeholder="Create a strong password (min. 6 characters)"
                  minLength="6"
                  className="form-input"
                />
                <div className="password-requirements">
                  <span>Password must be at least 6 characters long</span>
                </div>
              </div>
            </div>
            
            <button 
              type="submit" 
              disabled={loading} 
              className={`auth-btn ${loading ? 'loading' : ''}`}
            >
              {loading ? (
                <>
                  <span className="spinner"></span>
                  Creating Account...
                </>
              ) : (
                'Create Account'
              )}
            </button>
          </form>
          
          <div className="auth-footer">
            <p className="auth-link">
              Already have an account? <Link to="/login" className="link">Login here</Link>
            </p>
            <p className="auth-note">
              By creating an account, you agree to our Terms of Service and Privacy Policy.
            </p>
          </div>
        </div>
      </div>
    </>
  );
};

export default Register;
