import React, { createContext, useState, useContext, useEffect } from 'react';
import authService from '../services/authService';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Valid roles for role checking
  const validRoles = ['admin', 'technician', 'staff', 'student'];

  useEffect(() => {
    const token = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');
    
    if (token && savedUser) {
      try {
        const parsedUser = JSON.parse(savedUser);
        
        // Validate user role
        if (parsedUser.role && validRoles.includes(parsedUser.role)) {
          setUser(parsedUser);
          
          // Verify token is still valid
          authService.getCurrentUser()
            .then(userData => {
              // Ensure the role is valid
              if (userData.role && validRoles.includes(userData.role)) {
                setUser(userData);
                localStorage.setItem('user', JSON.stringify(userData));
              } else {
                logout();
              }
            })
            .catch(() => {
              logout();
            })
            .finally(() => {
              setLoading(false);
            });
        } else {
          // Invalid user role in storage, logout for security
          logout();
          setLoading(false);
        }
      } catch (error) {
        // Error parsing saved user, logout for security
        logout();
        setLoading(false);
      }
    } else {
      setLoading(false);
    }
  }, []);

  const login = async (email, password) => {
    const response = await authService.login(email, password);
    
    // Validate role before setting user
    if (response.user.role && validRoles.includes(response.user.role)) {
      setUser(response.user);
      localStorage.setItem('token', response.token);
      localStorage.setItem('user', JSON.stringify(response.user));
      return response;
    } else {
      throw new Error('Invalid user role received from server');
    }
  };

  const register = async (userData) => {
    const response = await authService.register(userData);
    
    // Validate role before setting user
    if (response.user.role && validRoles.includes(response.user.role)) {
      setUser(response.user);
      localStorage.setItem('token', response.token);
      localStorage.setItem('user', JSON.stringify(response.user));
      return response;
    } else {
      throw new Error('Invalid user role received from server');
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
  };

  // Helper function to check user permissions
  const hasRole = (roles) => {
    if (!user) return false;
    
    const rolesToCheck = Array.isArray(roles) ? roles : [roles];
    return rolesToCheck.includes(user.role);
  };

  const value = {
    user,
    login,
    register,
    logout,
    loading,
    hasRole // Add helper function for role checking
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
