import api from './api';

const userService = {
  // ==================== EXISTING METHODS (KEEP THESE) ====================
  
  getTechnicians: async () => {
    const response = await api.get('/users/technicians');
    return response.data;
  },

  getDashboardStats: async () => {
    const response = await api.get('/users/dashboard/stats');
    return response.data;
  },

  getDepartmentStats: async () => {
    const response = await api.get('/users/dashboard/department/stats');
    return response.data;
  },

  // ==================== ADMIN USER MANAGEMENT (FIXED ENDPOINTS) ====================

  // Get all users with advanced filtering (admin only) - FIXED ENDPOINT
  getAllUsers: async (filters = {}) => {
    const params = new URLSearchParams();
    Object.keys(filters).forEach(key => {
      if (filters[key] !== '' && filters[key] !== null && filters[key] !== undefined) {
        params.append(key, filters[key]);
      }
    });
    
    const response = await api.get(`/users/admin/users?${params}`);
    return response.data;
  },

  // Admin-only user creation - FIXED ENDPOINT
  createUser: async (userData) => {
    const response = await api.post('/users/admin/users', userData);
    return response.data;
  },

  // Admin-only user updates - FIXED ENDPOINT
  updateUser: async (userId, updates) => {
    const response = await api.put(`/users/admin/users/${userId}`, updates);
    return response.data;
  },

  // Admin-only user deletion - FIXED ENDPOINT
  deleteUser: async (userId) => {
    const response = await api.delete(`/users/admin/users/${userId}`);
    return response.data;
  },

  // Get user by ID (admin only) - FIXED ENDPOINT
  getUserById: async (userId) => {
    const response = await api.get(`/users/admin/users/${userId}`);
    return response.data;
  },

  // ==================== STATISTICS (FIXED ENDPOINTS) ====================

  // User statistics for admin dashboard - FIXED ENDPOINT
  getUserStats: async () => {
    const response = await api.get('/users/admin/stats');
    return response.data;
  },

  // Comprehensive system statistics (admin only) - FIXED ENDPOINT
  getSystemStats: async () => {
    const response = await api.get('/users/admin/system-stats');
    return response.data;
  },

  // ==================== USER PROFILE MANAGEMENT (KEEP THESE) ====================

  getCurrentUser: async () => {
    const response = await api.get('/users/me');
    return response.data;
  },

  updateProfile: async (updates) => {
    const response = await api.put('/users/profile', updates);
    return response.data;
  },

  changePassword: async (currentPassword, newPassword) => {
    const response = await api.put('/users/change-password', {
      currentPassword,
      newPassword
    });
    return response.data;
  },

  // ==================== REMOVE OR COMMENT OUT NON-EXISTENT ENDPOINTS ====================

  /*
  // THESE ENDPOINTS DON'T EXIST IN YOUR BACKEND - REMOVE OR COMMENT OUT
  
  getTechnicianStats: async (timeRange = '7d') => {
    const response = await api.get(`/dashboard/technician/stats?range=${timeRange}`);
    return response.data;
  },

  bulkUpdateUsers: async (userIds, updates) => {
    const response = await api.patch('/users/admin/bulk-update', { 
      userIds, 
      updates 
    });
    return response.data;
  },

  bulkDeleteUsers: async (userIds) => {
    const response = await api.post('/users/admin/bulk-delete', { userIds });
    return response.data;
  },

  bulkChangeRole: async (userIds, newRole) => {
    const response = await api.patch('/users/admin/bulk-role', {
      userIds,
      role: newRole
    });
    return response.data;
  },

  updateUserRole: async (userId, newRole) => {
    const response = await api.patch(`/users/${userId}/role`, { role: newRole });
    return response.data;
  },

  assignTechnicianRole: async (userId, department = '') => {
    const response = await api.post('/users/admin/assign-technician', {
      userId,
      department
    });
    return response.data;
  },

  revokeTechnicianRole: async (userId) => {
    const response = await api.post('/users/admin/revoke-technician', { userId });
    return response.data;
  },

  getRoleStatistics: async () => {
    const response = await api.get('/users/admin/role-stats');
    return response.data;
  },

  getSystemHealth: async () => {
    const response = await api.get('/system/health');
    return response.data;
  },

  getAllDepartments: async () => {
    const response = await api.get('/users/admin/departments');
    return response.data;
  },

  createDepartment: async (departmentData) => {
    const response = await api.post('/users/admin/departments', departmentData);
    return response.data;
  },

  updateDepartment: async (departmentId, updates) => {
    const response = await api.put(`/users/admin/departments/${departmentId}`, updates);
    return response.data;
  },

  deleteDepartment: async (departmentId) => {
    const response = await api.delete(`/users/admin/departments/${departmentId}`);
    return response.data;
  },

  getPerformanceMetrics: async (userId, period = 'month') => {
    const response = await api.get(`/users/${userId}/performance?period=${period}`);
    return response.data;
  },

  getTeamPerformance: async (period = 'month') => {
    const response = await api.get(`/team/performance?period=${period}`);
    return response.data;
  },

  getSystemPerformance: async (period = 'month') => {
    const response = await api.get(`/users/admin/performance?period=${period}`);
    return response.data;
  },

  getTechnicianPerformanceReport: async (period = 'month') => {
    const response = await api.get(`/users/admin/technician-performance?period=${period}`);
    return response.data;
  },

  getQuickStats: async () => {
    const response = await api.get('/dashboard/quick-stats');
    return response.data;
  },

  getAdminQuickStats: async () => {
    const response = await api.get('/users/admin/quick-stats');
    return response.data;
  },

  getNotificationPreferences: async () => {
    const response = await api.get('/users/notifications/preferences');
    return response.data;
  },

  updateNotificationPreferences: async (preferences) => {
    const response = await api.put('/users/notifications/preferences', preferences);
    return response.data;
  },

  sendBulkNotification: async (notificationData) => {
    const response = await api.post('/users/admin/bulk-notification', notificationData);
    return response.data;
  },

  getNotificationTemplates: async () => {
    const response = await api.get('/users/admin/notification-templates');
    return response.data;
  },

  getUserActivity: async (userId, filters = {}) => {
    const params = new URLSearchParams();
    Object.keys(filters).forEach(key => {
      if (filters[key]) params.append(key, filters[key]);
    });
    
    const response = await api.get(`/users/${userId}/activity?${params}`);
    return response.data;
  },

  getSystemActivityLogs: async (filters = {}) => {
    const params = new URLSearchParams();
    Object.keys(filters).forEach(key => {
      if (filters[key]) params.append(key, filters[key]);
    });
    
    const response = await api.get(`/users/admin/activity-logs?${params}`);
    return response.data;
  },

  getLoginHistory: async (userId) => {
    const response = await api.get(`/users/${userId}/login-history`);
    return response.data;
  },

  searchUsers: async (query, filters = {}) => {
    const params = new URLSearchParams();
    params.append('q', query);
    Object.keys(filters).forEach(key => {
      if (filters[key]) params.append(key, filters[key]);
    });
    
    const response = await api.get(`/users/search?${params}`);
    return response.data;
  },

  advancedUserSearch: async (criteria = {}) => {
    const response = await api.post('/users/admin/advanced-search', criteria);
    return response.data;
  },

  suspendUser: async (userId, reason = '', duration = null) => {
    const response = await api.post('/users/admin/suspend', {
      userId,
      reason,
      duration
    });
    return response.data;
  },

  unsuspendUser: async (userId) => {
    const response = await api.post('/users/admin/unsuspend', { userId });
    return response.data;
  },

  deactivateUser: async (userId, reason = '') => {
    const response = await api.post('/users/admin/deactivate', {
      userId,
      reason
    });
    return response.data;
  },

  activateUser: async (userId) => {
    const response = await api.post('/users/admin/activate', { userId });
    return response.data;
  },

  resetUserPassword: async (userId) => {
    const response = await api.post('/users/admin/reset-password', { userId });
    return response.data;
  },

  exportUsers: async (filters = {}, format = 'csv') => {
    const params = new URLSearchParams();
    Object.keys(filters).forEach(key => {
      if (filters[key]) params.append(key, filters[key]);
    });
    params.append('format', format);
    
    const response = await api.get(`/users/export?${params}`, {
      responseType: 'blob'
    });
    return response.data;
  },

  exportAllUsers: async (filters = {}, format = 'csv') => {
    const params = new URLSearchParams();
    Object.keys(filters).forEach(key => {
      if (filters[key]) params.append(key, filters[key]);
    });
    params.append('format', format);
    
    const response = await api.get(`/users/admin/export?${params}`, {
      responseType: 'blob'
    });
    return response.data;
  },

  debugGetAllUsers: async () => {
    const response = await api.get('/users/debug/all');
    return response.data;
  },

  runUserCleanup: async () => {
    const response = await api.post('/users/admin/cleanup');
    return response.data;
  },
  */

  // ==================== UTILITY METHODS (KEEP THESE) ====================

  // Validate user data
  validateUserData: (userData) => {
    const errors = [];
    
    if (!userData.username || userData.username.trim().length < 3) {
      errors.push('Username must be at least 3 characters long');
    }
    
    if (!userData.email || !/\S+@\S+\.\S+/.test(userData.email)) {
      errors.push('Valid email address is required');
    }
    
    if (!userData.password || userData.password.length < 6) {
      errors.push('Password must be at least 6 characters long');
    }
    
    if (!userData.role) {
      errors.push('User role is required');
    }
    
    return errors;
  },

  // Format user for display
  formatUser: (user) => {
    return {
      ...user,
      formattedCreatedAt: new Date(user.createdAt).toLocaleDateString(),
      formattedLastLogin: user.lastLogin ? new Date(user.lastLogin).toLocaleDateString() : 'Never',
      status: user.isActive ? (user.isVerified ? 'Active' : 'Pending Verification') : 'Inactive',
      roleDisplay: user.role?.charAt(0).toUpperCase() + user.role?.slice(1)
    };
  },

  // Check if user has admin privileges
  isAdmin: (user) => {
    return user?.role === 'admin';
  },

  // Check if user can manage other users
  canManageUsers: (user) => {
    return ['admin', 'technician'].includes(user?.role);
  }
};

export default userService;
