import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Navbar from '../components/Navbar';
import TicketCard from '../components/TicketCard';
import ticketService from '../services/ticketService';
import '../styles/Dashboard.css';

const StudentDashboard = () => {
  const [myTickets, setMyTickets] = useState([]);
  const [stats, setStats] = useState({
    total: 0,
    open: 0,
    inProgress: 0,
    resolved: 0
  });
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('all');

  useEffect(() => {
    fetchMyTickets();
  }, []);

  const fetchMyTickets = async () => {
    try {
      const response = await ticketService.getTickets({ 
        limit: 8,
        sortBy: 'createdAt', 
        sortOrder: 'desc'
      });
      
      setMyTickets(response.tickets);
      
      // Calculate simple stats
      const total = response.tickets.length;
      const open = response.tickets.filter(t => t.status === 'open').length;
      const inProgress = response.tickets.filter(t => t.status === 'in-progress').length;
      const resolved = response.tickets.filter(t => t.status === 'resolved' || t.status === 'closed').length;
      
      setStats({ total, open, inProgress, resolved });
    } catch (error) {
      console.error('Error fetching tickets:', error);
    } finally {
      setLoading(false);
    }
  };

  const quickHelpItems = [
    {
      title: 'WiFi Connection',
      description: 'Troubleshoot internet issues',
      icon: 'üì∂',
      path: '/knowledge-base/wifi'
    },
    {
      title: 'Password Reset',
      description: 'Reset your campus password',
      icon: 'üîë',
      path: '/knowledge-base/password'
    },
    {
      title: 'Email Setup',
      description: 'Configure student email',
      icon: 'üìß',
      path: '/knowledge-base/email'
    },
    {
      title: 'Software Help',
      description: 'Install and use campus software',
      icon: 'üíª',
      path: '/knowledge-base/software'
    }
  ];

  const filteredTickets = myTickets.filter(ticket => {
    if (activeTab === 'all') return true;
    return ticket.status === activeTab;
  });

  if (loading) {
    return (
      <>
        <Navbar />
        <div className="loading">Loading your dashboard...</div>
      </>
    );
  }

  return (
    <>
      <Navbar />
      <div className="dashboard student-dashboard">
        {/* Welcome Header */}
        <div className="dashboard-header">
          <div className="welcome-section">
            <h1>Student Support Portal</h1>
            <p className="welcome-message">
              Get help with technology issues and access campus resources
            </p>
          </div>
          <div className="header-actions">
            <Link to="/create-ticket" className="btn-primary">
              Get Help Now
            </Link>
          </div>
        </div>

        {/* Quick Help Section */}
        <div className="quick-help-section">
          <h2>Quick Help & Resources</h2>
          <div className="quick-help-grid">
            {quickHelpItems.map((item, index) => (
              <Link key={index} to={item.path} className="help-card">
                <div className="help-icon">{item.icon}</div>
                <div className="help-content">
                  <h4>{item.title}</h4>
                  <p>{item.description}</p>
                </div>
                <div className="help-arrow">‚Üí</div>
              </Link>
            ))}
          </div>
        </div>

        {/* My Tickets Overview */}
        <div className="my-tickets-section">
          <div className="section-header">
            <h2>My Support Requests</h2>
            <div className="ticket-tabs">
              <button 
                className={`tab-button ${activeTab === 'all' ? 'active' : ''}`}
                onClick={() => setActiveTab('all')}
              >
                All ({stats.total})
              </button>
              <button 
                className={`tab-button ${activeTab === 'open' ? 'active' : ''}`}
                onClick={() => setActiveTab('open')}
              >
                Open ({stats.open})
              </button>
              <button 
                className={`tab-button ${activeTab === 'in-progress' ? 'active' : ''}`}
                onClick={() => setActiveTab('in-progress')}
              >
                In Progress ({stats.inProgress})
              </button>
              <button 
                className={`tab-button ${activeTab === 'resolved' ? 'active' : ''}`}
                onClick={() => setActiveTab('resolved')}
              >
                Resolved ({stats.resolved})
              </button>
            </div>
          </div>

          {filteredTickets.length === 0 ? (
            <div className="empty-state">
              <div className="empty-icon">üìã</div>
              <h3>No Tickets Found</h3>
              <p>
                {activeTab === 'all' 
                  ? "You haven't created any support tickets yet."
                  : `You don't have any ${activeTab.replace('-', ' ')} tickets.`
                }
              </p>
              <Link to="/create-ticket" className="btn-primary">
                Create Your First Ticket
              </Link>
            </div>
          ) : (
            <div className="tickets-grid">
              {filteredTickets.map(ticket => (
                <TicketCard key={ticket._id} ticket={ticket} />
              ))}
            </div>
          )}

          {myTickets.length > 0 && (
            <div className="view-all">
              <Link to="/tickets" className="btn-secondary">
                View All My Tickets
              </Link>
            </div>
          )}
        </div>

        {/* Support Contact Info */}
        <div className="support-info">
          <div className="support-card">
            <h3>Need Immediate Help?</h3>
            <div className="contact-methods">
              <div className="contact-item">
                <span className="contact-icon">üìû</span>
                <div className="contact-details">
                  <strong>Phone Support</strong>
                  <span>Call: (555) 123-HELP</span>
                </div>
              </div>
              <div className="contact-item">
                <span className="contact-icon">üí¨</span>
                <div className="contact-details">
                  <strong>Live Chat</strong>
                  <span>Available 8AM-8PM</span>
                </div>
              </div>
              <div className="contact-item">
                <span className="contact-icon">üè¢</span>
                <div className="contact-details">
                  <strong>Walk-in Help</strong>
                  <span>Library Building, Room 205</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Quick Stats */}
        <div className="quick-stats">
          <div className="stat-cards">
            <div className="stat-card">
              <div className="stat-number">{stats.open}</div>
              <div className="stat-label">Open Tickets</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">{stats.inProgress}</div>
              <div className="stat-label">In Progress</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">{stats.resolved}</div>
              <div className="stat-label">Resolved</div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default StudentDashboard;
