import React, { createContext, useState, useContext, useEffect, useCallback, useRef } from 'react';
import PropTypes from 'prop-types';
import authService from '../services/authService';
import api from '../services/api';

/**
 * AuthContext - Enterprise Authentication System
 * 
 * Features:
 * - JWT token management with auto-refresh
 * - Session validation and monitoring
 * - Role-based access control
 * - Activity logging
 * - Security event tracking
 * - Concurrent session prevention
 * - Token refresh queue
 * - Graceful error handling
 * 
 * @version 4.2.0
 * @author IT Support Team
 */

export const AuthContext = createContext();

/**
 * Custom hook to access authentication context
 * 
 * @returns {Object} Authentication context
 * @throws {Error} If used outside AuthProvider
 */
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

/**
 * Validates user data structure
 * 
 * @param {Object} userData - User data to validate
 * @returns {boolean} True if valid
 */
const validateUserData = (userData) => {
  if (!userData || typeof userData !== 'object') {
    return false;
  }

  // Required fields
  const requiredFields = ['_id', 'email', 'role'];
  for (const field of requiredFields) {
    if (!userData[field]) {
      console.warn(`Invalid user data: Missing ${field}`);
      return false;
    }
  }

  // Validate role
  const validRoles = ['admin', 'technician', 'staff', 'student'];
  if (!validRoles.includes(userData.role)) {
    console.warn(`Invalid user data: Invalid role "${userData.role}"`);
    return false;
  }

  // Validate email format
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!emailRegex.test(userData.email)) {
    console.warn(`Invalid user data: Invalid email "${userData.email}"`);
    return false;
  }

  return true;
};

/**
 * Sanitizes user data before storage
 * 
 * @param {Object} userData - Raw user data
 * @returns {Object} Sanitized user data
 */
const sanitizeUserData = (userData) => {
  if (!userData) return null;
  
  const sanitized = { ...userData };
  
  // Remove sensitive fields
  delete sanitized.password;
  delete sanitized.tempPassword;
  delete sanitized.resetToken;
  delete sanitized.verificationToken;
  delete sanitized.__v;
  
  // Ensure role is lowercase
  if (sanitized.role) {
    sanitized.role = sanitized.role.toLowerCase();
  }
  
  return sanitized;
};

/**
 * AuthProvider Component
 * 
 * @param {Object} props - Component properties
 * @param {ReactNode} props.children - Child components
 */
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [sessionValid, setSessionValid] = useState(false);
  const [sessionExpiresAt, setSessionExpiresAt] = useState(null);
  const [authError, setAuthError] = useState(null);
  const [authSuccess, setAuthSuccess] = useState(null);
  
  const refreshTimeoutRef = useRef(null);
  const sessionCheckIntervalRef = useRef(null);
  const isRefreshingRef = useRef(false);
  const refreshQueueRef = useRef([]);

  /**
   * Clears authentication errors and success messages
   */
  const clearError = useCallback(() => {
    setAuthError(null);
  }, []);

  const clearSuccess = useCallback(() => {
    setAuthSuccess(null);
  }, []);

  /**
   * Sets authentication error
   */
  const setError = useCallback((message) => {
    setAuthError(message);
    // Auto-clear error after 10 seconds
    setTimeout(() => {
      setAuthError(null);
    }, 10000);
  }, []);

  /**
   * Sets authentication success message
   */
  const setSuccess = useCallback((message) => {
    setAuthSuccess(message);
    // Auto-clear success after 5 seconds
    setTimeout(() => {
      setAuthSuccess(null);
    }, 5000);
  }, []);

  /**
   * Initializes authentication state from storage
   */
  const initializeAuth = useCallback(async () => {
    try {
      setLoading(true);
      setAuthError(null);
      setAuthSuccess(null);

      const token = localStorage.getItem('token') || sessionStorage.getItem('token');
      const savedUser = localStorage.getItem('user') || sessionStorage.getItem('user');
      const sessionExpiry = localStorage.getItem('sessionExpiry');

      if (!token || !savedUser) {
        setLoading(false);
        return;
      }

      // Parse and validate saved user
      let parsedUser;
      try {
        parsedUser = JSON.parse(savedUser);
      } catch (error) {
        console.error('Error parsing saved user:', error);
        logout('Invalid session data');
        return;
      }

      // Validate user data
      if (!validateUserData(parsedUser)) {
        console.warn('Invalid user data in storage, logging out');
        logout('Invalid user data');
        return;
      }

      // Check session expiry
      if (sessionExpiry) {
        const expiryTime = parseInt(sessionExpiry, 10);
        if (Date.now() > expiryTime) {
          console.warn('Session expired, logging out');
          logout('Session expired');
          return;
        }
        setSessionExpiresAt(expiryTime);
      }

      // Set token in API headers
      api.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // Verify token with backend
      try {
        const response = await authService.getCurrentUser();
        
        if (!response.success) {
          throw new Error(response.message || 'Failed to get current user');
        }

        const userData = response.data;
        
        if (!validateUserData(userData)) {
          throw new Error('Invalid user data received from server');
        }

        const sanitizedUser = sanitizeUserData(userData);
        setUser(sanitizedUser);
        setSessionValid(true);
        
        // Update storage with fresh data
        const storage = localStorage.getItem('token') ? localStorage : sessionStorage;
        storage.setItem('user', JSON.stringify(sanitizedUser));
        
        // Schedule token refresh
        scheduleTokenRefresh();
        
        // Start session monitoring
        startSessionMonitoring();
        
        console.log('‚úÖ Auth initialized successfully:', sanitizedUser.email);
        
      } catch (error) {
        console.error('Token verification failed:', error);
        
        // Try to refresh token
        try {
          await refreshToken();
        } catch (refreshError) {
          console.error('Token refresh failed:', refreshError);
          logout('Session verification failed');
        }
      }
    } catch (error) {
      console.error('Auth initialization error:', error);
      setError('Authentication initialization failed');
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Refreshes authentication token
   */
  const refreshToken = useCallback(async () => {
    // Prevent multiple concurrent refresh attempts
    if (isRefreshingRef.current) {
      return new Promise((resolve, reject) => {
        refreshQueueRef.current.push({ resolve, reject });
      });
    }

    try {
      isRefreshingRef.current = true;
      
      const response = await authService.refreshToken();
      
      if (!response.success || !response.data?.accessToken || !response.data?.user) {
        throw new Error('Invalid refresh response');
      }

      // Validate user data
      if (!validateUserData(response.data.user)) {
        throw new Error('Invalid user data in refresh response');
      }

      const sanitizedUser = sanitizeUserData(response.data.user);
      const accessToken = response.data.accessToken;
      
      // Update state and storage
      setUser(sanitizedUser);
      setSessionValid(true);
      
      const storage = localStorage.getItem('token') ? localStorage : sessionStorage;
      storage.setItem('token', accessToken);
      storage.setItem('user', JSON.stringify(sanitizedUser));
      
      // Update API headers
      api.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`;
      
      // Set new session expiry (24 hours from now)
      const expiryTime = Date.now() + (24 * 60 * 60 * 1000);
      setSessionExpiresAt(expiryTime);
      localStorage.setItem('sessionExpiry', expiryTime.toString());
      
      // Schedule next refresh
      scheduleTokenRefresh();
      
      // Process queued requests
      while (refreshQueueRef.current.length > 0) {
        const queued = refreshQueueRef.current.shift();
        queued.resolve();
      }
      
      return response;
    } catch (error) {
      console.error('Token refresh failed:', error);
      
      // Process queued requests with error
      while (refreshQueueRef.current.length > 0) {
        const queued = refreshQueueRef.current.shift();
        queued.reject(error);
      }
      
      throw error;
    } finally {
      isRefreshingRef.current = false;
    }
  }, []);

  /**
   * Schedules token refresh before expiry
   */
  const scheduleTokenRefresh = useCallback(() => {
    // Clear existing timeout
    if (refreshTimeoutRef.current) {
      clearTimeout(refreshTimeoutRef.current);
    }

    // Schedule refresh 5 minutes before expiry
    const refreshTime = sessionExpiresAt 
      ? sessionExpiresAt - (5 * 60 * 1000) - Date.now()
      : 55 * 60 * 1000; // Default: 55 minutes

    if (refreshTime > 0) {
      refreshTimeoutRef.current = setTimeout(async () => {
        try {
          await refreshToken();
        } catch (error) {
          console.error('Scheduled token refresh failed:', error);
          // Don't logout immediately, let session monitoring handle it
        }
      }, refreshTime);
    }
  }, [sessionExpiresAt, refreshToken]);

  /**
   * Starts session monitoring
   */
  const startSessionMonitoring = useCallback(() => {
    // Clear existing interval
    if (sessionCheckIntervalRef.current) {
      clearInterval(sessionCheckIntervalRef.current);
    }

    // Check session every minute
    sessionCheckIntervalRef.current = setInterval(() => {
      if (sessionExpiresAt && Date.now() > sessionExpiresAt) {
        console.warn('Session expired during monitoring');
        logout('Session expired');
      }
    }, 60 * 1000);
  }, [sessionExpiresAt]);

  /**
   * Handles user login
   */
  const login = useCallback(async (email, password, rememberMe = false) => {
    try {
      setAuthError(null);
      setAuthSuccess(null);
      setLoading(true);

      console.log('üîê Attempting login for:', email);
      
      const response = await authService.login(email, password, rememberMe);
      
      console.log('üîê Login response:', response);
      
      if (!response.success) {
        throw new Error(response.message || 'Login failed');
      }

      if (!response.data?.user || !response.data?.accessToken) {
        throw new Error('Invalid login response format');
      }

      // Validate user data
      if (!validateUserData(response.data.user)) {
        throw new Error('Invalid user data received from server');
      }

      const sanitizedUser = sanitizeUserData(response.data.user);
      const accessToken = response.data.accessToken;
      
      // Update state
      setUser(sanitizedUser);
      setSessionValid(true);
      
      // Calculate session expiry
      const expiryTime = Date.now() + (24 * 60 * 60 * 1000); // 24 hours
      setSessionExpiresAt(expiryTime);
      
      // Choose storage based on rememberMe
      const storage = rememberMe ? localStorage : sessionStorage;
      storage.setItem('token', accessToken);
      storage.setItem('user', JSON.stringify(sanitizedUser));
      
      if (rememberMe) {
        localStorage.setItem('sessionExpiry', expiryTime.toString());
        localStorage.setItem('rememberMe', 'true');
      }

      // Update API headers
      api.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`;

      // Schedule token refresh
      scheduleTokenRefresh();
      
      // Start session monitoring
      startSessionMonitoring();
      
      // Log successful login
      logSecurityEvent('login_success', { email, role: sanitizedUser.role });
      
      console.log('‚úÖ Login successful:', sanitizedUser.email);
      
      return response;
    } catch (error) {
      console.error('‚ùå Login error:', error);
      
      // Log failed login attempt
      logSecurityEvent('login_failed', { email, error: error.message });
      
      const errorMessage = error.response?.data?.message || 
                          error.message || 
                          'Login failed. Please check your credentials.';
      
      setError(errorMessage);
      throw error;
    } finally {
      setLoading(false);
    }
  }, [scheduleTokenRefresh, startSessionMonitoring, setError, logSecurityEvent]);

  /**
   * Handles user registration
   */
  const register = useCallback(async (userData) => {
    try {
      setAuthError(null);
      setAuthSuccess(null);
      setLoading(true);

      console.log('üìù Attempting registration for:', userData.email);
      console.log('üìù Registration data:', { 
        username: userData.username,
        email: userData.email,
        role: userData.role,
        department: userData.department,
        studentId: userData.studentId 
      });
      
      const response = await authService.register(userData);
      
      console.log('üìù Registration response:', response);
      
      if (!response.success) {
        throw new Error(response.message || 'Registration failed');
      }

      // Check if email verification is required
      const requiresVerification = response.data?.requiresEmailVerification || false;
      
      // For registration, we might not get a token immediately if email verification is required
      if (requiresVerification) {
        // Don't log in automatically, just show success message
        console.log('üìß Email verification required');
        setSuccess('Registration successful! Please check your email to verify your account.');
        
        // Log successful registration (pending verification)
        logSecurityEvent('registration_pending_verification', { 
          email: userData.email, 
          role: userData.role 
        });
        
        return {
          ...response,
          requiresVerification: true,
          message: 'Registration successful! Please check your email to verify your account.'
        };
      }

      // If no verification required, proceed with auto-login
      if (!response.data?.user || !response.data?.accessToken) {
        throw new Error('Invalid registration response format');
      }

      // Validate user data
      if (!validateUserData(response.data.user)) {
        throw new Error('Invalid user data received from server');
      }

      const sanitizedUser = sanitizeUserData(response.data.user);
      const accessToken = response.data.accessToken;
      
      // Update state
      setUser(sanitizedUser);
      setSessionValid(true);
      
      // Calculate session expiry
      const expiryTime = Date.now() + (24 * 60 * 60 * 1000); // 24 hours
      setSessionExpiresAt(expiryTime);
      
      // Store in localStorage by default for new registrations
      localStorage.setItem('token', accessToken);
      localStorage.setItem('user', JSON.stringify(sanitizedUser));
      localStorage.setItem('sessionExpiry', expiryTime.toString());

      // Update API headers
      api.defaults.headers.common['Authorization'] = `Bearer ${accessToken}`;

      // Schedule token refresh
      scheduleTokenRefresh();
      
      // Start session monitoring
      startSessionMonitoring();
      
      // Log successful registration
      logSecurityEvent('registration_success', { 
        email: sanitizedUser.email, 
        role: sanitizedUser.role 
      });
      
      console.log('‚úÖ Registration successful:', sanitizedUser.email);
      
      return {
        ...response,
        requiresVerification: false,
        message: 'Registration successful! You are now logged in.'
      };
    } catch (error) {
      console.error('‚ùå Registration error:', error);
      console.error('‚ùå Error details:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status
      });
      
      // Log failed registration
      logSecurityEvent('registration_failed', { 
        email: userData.email, 
        error: error.message,
        role: userData.role
      });
      
      let errorMessage = 'Registration failed. Please try again.';
      
      // Extract specific error from authService response
      if (error.message && error.message !== 'Registration failed. Please try again.') {
        errorMessage = error.message;
      } else if (error.response?.data?.message) {
        errorMessage = error.response.data.message;
      } else if (error.response?.data?.errors) {
        // Handle validation errors
        const validationErrors = error.response.data.errors;
        if (Array.isArray(validationErrors)) {
          errorMessage = validationErrors.map(err => err.message || err.msg).join('. ');
        } else if (typeof validationErrors === 'object') {
          errorMessage = Object.values(validationErrors).join('. ');
        }
      }
      
      setError(errorMessage);
      throw new Error(errorMessage);
    } finally {
      setLoading(false);
    }
  }, [scheduleTokenRefresh, startSessionMonitoring, setError, setSuccess, logSecurityEvent]);

  /**
   * Handles user logout
   */
  const logout = useCallback((reason = 'user_initiated') => {
    // Clear state
    setUser(null);
    setSessionValid(false);
    setSessionExpiresAt(null);
    setAuthError(null);
    setAuthSuccess(null);
    
    // Clear timeouts and intervals
    if (refreshTimeoutRef.current) {
      clearTimeout(refreshTimeoutRef.current);
      refreshTimeoutRef.current = null;
    }
    
    if (sessionCheckIntervalRef.current) {
      clearInterval(sessionCheckIntervalRef.current);
      sessionCheckIntervalRef.current = null;
    }
    
    // Clear storage
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('sessionExpiry');
    localStorage.removeItem('rememberMe');
    sessionStorage.removeItem('token');
    sessionStorage.removeItem('user');
    
    // Clear API authorization header
    delete api.defaults.headers.common['Authorization'];
    
    // Call backend logout if possible
    authService.logout().catch(error => {
      console.error('Backend logout failed:', error);
    });
    
    // Log logout event
    logSecurityEvent('logout', { reason });
    
    // Clear refresh queue
    refreshQueueRef.current = [];
    
    console.log('üëã User logged out:', reason);
  }, [logSecurityEvent]);

  /**
   * Logs security events for monitoring
   */
  const logSecurityEvent = useCallback((eventType, data = {}) => {
    const securityEvent = {
      timestamp: new Date().toISOString(),
      event: eventType,
      userId: user?._id,
      userRole: user?.role,
      ...data,
      userAgent: navigator.userAgent,
      ip: 'client-ip'
    };

    // Log to console in development
    if (process.env.NODE_ENV === 'development') {
      console.log('üîí Security Event:', securityEvent);
    }

    // Send to security monitoring service
    sendToSecurityLog(securityEvent);
  }, [user]);

  /**
   * Sends security events to monitoring service
   */
  const sendToSecurityLog = useCallback((eventData) => {
    try {
      // Store in localStorage for debugging (remove in production)
      const existingEvents = JSON.parse(localStorage.getItem('security_events') || '[]');
      existingEvents.push(eventData);
      
      // Keep only last 50 events
      if (existingEvents.length > 50) {
        existingEvents.shift();
      }
      
      localStorage.setItem('security_events', JSON.stringify(existingEvents));
    } catch (e) {
      // Silently fail if localStorage is not available
    }
  }, []);

  /**
   * Checks if user has required role(s)
   */
  const hasRole = useCallback((roles) => {
    if (!user || !user.role) return false;
    
    const rolesToCheck = Array.isArray(roles) ? roles : [roles];
    return rolesToCheck.includes(user.role);
  }, [user]);

  /**
   * Checks if user has required permission(s)
   */
  const hasPermission = useCallback((permissions) => {
    if (!user || !user.permissions) return false;
    
    const permissionsToCheck = Array.isArray(permissions) ? permissions : [permissions];
    return permissionsToCheck.every(permission => 
      user.permissions?.includes(permission)
    );
  }, [user]);

  /**
   * Checks if session is valid and active
   */
  const checkSession = useCallback(async () => {
    if (!user) return false;
    
    try {
      await authService.getCurrentUser();
      return true;
    } catch (error) {
      return false;
    }
  }, [user]);

  /**
   * Updates user profile
   */
  const updateProfile = useCallback(async (updates) => {
    try {
      const response = await authService.updateProfile(updates);
      
      if (response.success && response.data?.user) {
        const sanitizedUser = sanitizeUserData(response.data.user);
        setUser(sanitizedUser);
        
        const storage = localStorage.getItem('token') ? localStorage : sessionStorage;
        storage.setItem('user', JSON.stringify(sanitizedUser));
      }
      
      return response;
    } catch (error) {
      console.error('Profile update error:', error);
      throw error;
    }
  }, []);

  /**
   * Changes user password
   */
  const changePassword = useCallback(async (currentPassword, newPassword) => {
    try {
      const response = await authService.changePassword(currentPassword, newPassword);
      if (response.success) {
        logSecurityEvent('password_changed');
      }
      return response;
    } catch (error) {
      console.error('Password change error:', error);
      throw error;
    }
  }, [logSecurityEvent]);

  /**
   * Requests password reset
   */
  const forgotPassword = useCallback(async (email) => {
    try {
      return await authService.forgotPassword(email);
    } catch (error) {
      console.error('Forgot password error:', error);
      throw error;
    }
  }, []);

  /**
   * Resets password with token
   */
  const resetPassword = useCallback(async (token, userId, newPassword) => {
    try {
      return await authService.resetPassword(token, userId, newPassword);
    } catch (error) {
      console.error('Reset password error:', error);
      throw error;
    }
  }, []);

  /**
   * Verifies email with token
   */
  const verifyEmail = useCallback(async (token, userId) => {
    try {
      return await authService.verifyEmail(token, userId);
    } catch (error) {
      console.error('Email verification error:', error);
      throw error;
    }
  }, []);

  /**
   * Checks if email is available
   */
  const checkEmail = useCallback(async (email) => {
    try {
      return await authService.checkEmail(email);
    } catch (error) {
      console.error('Check email error:', error);
      throw error;
    }
  }, []);

  /**
   * Checks if username is available
   */
  const checkUsername = useCallback(async (username) => {
    try {
      return await authService.checkUsername(username);
    } catch (error) {
      console.error('Check username error:', error);
      throw error;
    }
  }, []);

  // Initialize authentication on mount
  useEffect(() => {
    initializeAuth();

    // Cleanup on unmount
    return () => {
      if (refreshTimeoutRef.current) {
        clearTimeout(refreshTimeoutRef.current);
      }
      if (sessionCheckIntervalRef.current) {
        clearInterval(sessionCheckIntervalRef.current);
      }
    };
  }, [initializeAuth]);

  // Set up axios interceptor for token refresh
  useEffect(() => {
    const interceptor = api.interceptors.response.use(
      (response) => response,
      async (error) => {
        const originalRequest = error.config;
        
        // If error is 401 and not a retry, try to refresh token
        if (error.response?.status === 401 && !originalRequest._retry) {
          originalRequest._retry = true;
          
          try {
            await refreshToken();
            
            // Retry the original request with new token
            const token = localStorage.getItem('token') || sessionStorage.getItem('token');
            if (token) {
              originalRequest.headers.Authorization = `Bearer ${token}`;
            }
            return api(originalRequest);
          } catch (refreshError) {
            // Refresh failed, logout user
            logout('token_refresh_failed');
            return Promise.reject(refreshError);
          }
        }
        
        return Promise.reject(error);
      }
    );

    // Cleanup interceptor
    return () => {
      api.interceptors.response.eject(interceptor);
    };
  }, [refreshToken, logout]);

  // Context value
  const value = {
    // State
    user,
    loading,
    sessionValid,
    sessionExpiresAt,
    authError,
    authSuccess,
    
    // Authentication methods
    login,
    register,
    logout,
    refreshToken,
    checkSession,
    
    // User management
    updateProfile,
    changePassword,
    forgotPassword,
    resetPassword,
    verifyEmail,
    
    // Validation
    checkEmail,
    checkUsername,
    
    // Access control
    hasRole,
    hasPermission,
    
    // Security logging
    logSecurityEvent,
    
    // Utility
    isAuthenticated: !!user && sessionValid,
    clearError,
    clearSuccess,
    setError,
    setSuccess
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

// PropTypes
AuthProvider.propTypes = {
  children: PropTypes.node.isRequired
};

// ‚úÖ Export both as named exports and default
export default AuthContext;
