import React, { useState } from 'react';
import { useUsers, useCreateUser, useUpdateUser, useDeleteUser } from '../../hooks/useAdminData';
import CreateUserModal from './CreateUserModal';
import EditUserModal from './EditUserModal'; 

const UserManagement = () => {
  const [filters, setFilters] = useState({});
  const [showCreateModal, setShowCreateModal] = useState(false);
   const [editingUser, setEditingUser] = useState(null); 
  const [viewingUser, setViewingUser] = useState(null); 
  
  const { data: usersData, isLoading, error, refetch } = useUsers(filters);
  const createUserMutation = useCreateUser();
  const updateUserMutation = useUpdateUser();
  const deleteUserMutation = useDeleteUser();

  const users = usersData?.users || [];
  const pagination = usersData || {};

  const handleCreateUser = async (userData) => {
    try {
      await createUserMutation.mutateAsync(userData);
      setShowCreateModal(false);
      refetch(); // Refresh the user list
    } catch (error) {
      console.error('Failed to create user:', error);
      alert('Error creating user: ' + error.message);
    }
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await deleteUserMutation.mutateAsync(userId);
        refetch(); // Refresh the user list
        alert('User deleted successfully');
      } catch (error) {
        console.error('Failed to delete user:', error);
        alert('Error deleting user: ' + error.message);
      }
    }
  };

  if (isLoading) return <div className="loading">Loading users...</div>;
  if (error) return <div className="error">Error loading users: {error.message}</div>;

  return (
    <div className="user-management">
      <div className="section-header">
        <h2>User Management</h2>
        <div className="header-actions">
          <button 
            className="btn-primary"
            onClick={() => setShowCreateModal(true)}
            disabled={createUserMutation.isLoading}
          >
            {createUserMutation.isLoading ? 'Creating...' : 'Create New User'}
          </button>
          <button 
            className="btn-secondary"
            onClick={refetch}
            disabled={isLoading}
          >
            Refresh
          </button>
        </div>
      </div>

      {/* Users table */}
      <div className="users-table">
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Email</th>
              <th>Role</th>
              <th>Department</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.length === 0 ? (
              <tr>
                <td colSpan="6" style={{ textAlign: 'center', padding: '20px' }}>
                  No users found
                </td>
              </tr>
            ) : (
              users.map(user => (
                <tr key={user._id}>
                  <td>{user.username}</td>
                  <td>{user.email}</td>
                  <td>
                    <span className={`role-badge ${user.role}`}>
                      {user.role}
                    </span>
                  </td>
                  <td>{user.department}</td>
                  <td>
                    <span className={`status-badge ${user.isActive ? 'active' : 'inactive'}`}>
                      {user.isActive ? 'Active' : 'Inactive'}
                    </span>
                  </td>
                  <td>
                    <button className="btn-sm">Edit</button>
                    <button 
                      className="btn-sm danger"
                      onClick={() => handleDeleteUser(user._id)}
                      disabled={deleteUserMutation.isLoading}
                    >
                      {deleteUserMutation.isLoading ? 'Deleting...' : 'Delete'}
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Pagination Info */}
      {pagination.totalPages > 1 && (
        <div className="pagination-info">
          <p>
            Showing {users.length} of {pagination.total} users
            {pagination.totalPages > 1 && ` (Page ${pagination.currentPage} of ${pagination.totalPages})`}
          </p>
        </div>
      )}

      {/* Create User Modal */}
      {showCreateModal && (
        <CreateUserModal 
          onSubmit={handleCreateUser}
          onClose={() => setShowCreateModal(false)}
          loading={createUserMutation.isLoading}
        />
      )}
    </div>
  );
};

export default UserManagement;
