// src/pages/CreateTicket.js
import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import Navbar from '../components/Navbar';
import api from '../services/api'; // Your API service
import './CreateTicket.css'; // Optional CSS

const CreateTicket = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: 'other',
    priority: 'medium',
    campus: user?.campus || 'BU',
    location: '',
    building: '',
    roomNumber: '',
    department: user?.department || 'computer_science'
  });
  
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [formErrors, setFormErrors] = useState({});

  // Redirect if not authenticated
  useEffect(() => {
    if (!user) {
      navigate('/login');
    }
  }, [user, navigate]);

  // Get user's full name
  const getUserName = () => {
    if (!user) return 'User';
    if (user.firstName && user.lastName) return `${user.firstName} ${user.lastName}`;
    if (user.firstName) return user.firstName;
    if (user.username) return user.username;
    return user.email?.split('@')[0] || 'User';
  };

  // Validate form
  const validateForm = () => {
    const errors = {};
    
    if (!formData.title.trim()) {
      errors.title = 'Title is required';
    } else if (formData.title.trim().length < 5) {
      errors.title = 'Title must be at least 5 characters';
    } else if (formData.title.trim().length > 200) {
      errors.title = 'Title cannot exceed 200 characters';
    }
    
    if (!formData.description.trim()) {
      errors.description = 'Description is required';
    } else if (formData.description.trim().length < 10) {
      errors.description = 'Description must be at least 10 characters';
    } else if (formData.description.trim().length > 5000) {
      errors.description = 'Description cannot exceed 5000 characters';
    }
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error for this field when user starts typing
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validate form
    if (!validateForm()) {
      setError('Please fix the errors in the form');
      return;
    }

    setIsSubmitting(true);

    try {
      console.log('üìù Creating ticket with data:', {
        ...formData,
        createdBy: user?._id
      });

      const response = await api.post('/api/tickets', {
        ...formData,
        createdBy: user?._id
      });

      console.log('‚úÖ Ticket created successfully:', response);

      if (response.data.success) {
        setSuccess('Ticket created successfully! Redirecting to dashboard...');
        
        // Clear form
        setFormData({
          title: '',
          description: '',
          category: 'other',
          priority: 'medium',
          campus: user?.campus || 'BU',
          location: '',
          building: '',
          roomNumber: '',
          department: user?.department || 'computer_science'
        });

        // Redirect after 2 seconds
        setTimeout(() => {
          navigate('/dashboard');
        }, 2000);
      } else {
        setError(response.data.message || 'Failed to create ticket');
      }
    } catch (err) {
      console.error('‚ùå Create ticket error:', err);
      
      // Handle specific error types
      if (err.name === 'AuthError') {
        setError('Your session has expired. Please log in again.');
        logout();
        navigate('/login');
        return;
      }
      
      if (err.name === 'NetworkError') {
        setError('Network error. Please check your internet connection and try again.');
        return;
      }
      
      if (err.name === 'APIError') {
        // Handle validation errors from backend
        if (err.status === 422 && err.data?.errors) {
          const backendErrors = {};
          err.data.errors.forEach(error => {
            if (error.field) {
              backendErrors[error.field] = error.message;
            }
          });
          setFormErrors(backendErrors);
          setError('Please fix the validation errors');
        } else {
          setError(err.message || 'An error occurred. Please try again.');
        }
        return;
      }
      
      setError('An unexpected error occurred. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!user) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Redirecting to login...</p>
      </div>
    );
  }

  return (
    <div className="create-ticket-page">
      <Navbar />
      
      <div className="create-ticket-container">
        <div className="create-ticket-header">
          <h1>Create New Ticket</h1>
          <p className="welcome-message">
            Welcome, <strong>{getUserName()}</strong>. Fill out the form below to create a new support ticket.
          </p>
        </div>
        
        {error && (
          <div className="alert alert-error">
            <strong>Error:</strong> {error}
          </div>
        )}
        
        {success && (
          <div className="alert alert-success">
            <strong>Success!</strong> {success}
          </div>
        )}
        
        <div className="create-ticket-card">
          <form onSubmit={handleSubmit}>
            {/* Title Field */}
            <div className="form-group">
              <label htmlFor="title">
                Title <span className="required">*</span>
              </label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleChange}
                placeholder="Brief description of the issue"
                className={formErrors.title ? 'input-error' : ''}
                disabled={isSubmitting}
              />
              {formErrors.title && (
                <div className="error-message">{formErrors.title}</div>
              )}
              <div className="form-help">
                Be specific about the issue (e.g., "WiFi not working in Library Building")
              </div>
            </div>
            
            {/* Description Field */}
            <div className="form-group">
              <label htmlFor="description">
                Description <span className="required">*</span>
              </label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleChange}
                placeholder="Provide detailed information about the issue..."
                rows="6"
                className={formErrors.description ? 'input-error' : ''}
                disabled={isSubmitting}
              />
              {formErrors.description && (
                <div className="error-message">{formErrors.description}</div>
              )}
              <div className="form-help">
                Include: What happened, when it happened, error messages, steps to reproduce
              </div>
            </div>
            
            <div className="form-row">
              {/* Category Field */}
              <div className="form-group">
                <label htmlFor="category">
                  Category <span className="required">*</span>
                </label>
                <select
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  disabled={isSubmitting}
                >
                  <option value="network">üåê Network Issues</option>
                  <option value="software">üíª Software Issues</option>
                  <option value="hardware">üîß Hardware Issues</option>
                  <option value="account">üë§ Account Issues</option>
                  <option value="email">üìß Email Issues</option>
                  <option value="website">üåç Website Issues</option>
                  <option value="other">‚ùì Other Issues</option>
                </select>
              </div>
              
              {/* Priority Field */}
              <div className="form-group">
                <label htmlFor="priority">
                  Priority <span className="required">*</span>
                </label>
                <select
                  id="priority"
                  name="priority"
                  value={formData.priority}
                  onChange={handleChange}
                  disabled={isSubmitting}
                >
                  <option value="low">üü¢ Low (72 hours response)</option>
                  <option value="medium">üü° Medium (24 hours response)</option>
                  <option value="high">üü† High (8 hours response)</option>
                  <option value="critical">üî¥ Critical (4 hours response)</option>
                </select>
              </div>
            </div>
            
            {/* Campus Field */}
            <div className="form-group">
              <label htmlFor="campus">
                Campus <span className="required">*</span>
              </label>
              <select
                id="campus"
                name="campus"
                value={formData.campus}
                onChange={handleChange}
                disabled={isSubmitting}
              >
                <option value="BU">üèõÔ∏è Main Campus (BU) - Luweero</option>
                <option value="MA">üè¢ Mabanga Campus (MA)</option>
                <option value="KA">üèôÔ∏è Kampala Campus (KA)</option>
                <option value="AR">üèòÔ∏è Arua Campus (AR)</option>
                <option value="MB">üè¢ Mbarara Campus (MB)</option>
                <option value="OTHER">üìç Other Location</option>
              </select>
            </div>
            
            <div className="form-row">
              {/* Building Field */}
              <div className="form-group">
                <label htmlFor="building">Building</label>
                <input
                  type="text"
                  id="building"
                  name="building"
                  value={formData.building}
                  onChange={handleChange}
                  placeholder="e.g., Library, Admin Block"
                  disabled={isSubmitting}
                />
              </div>
              
              {/* Room Number Field */}
              <div className="form-group">
                <label htmlFor="roomNumber">Room Number</label>
                <input
                  type="text"
                  id="roomNumber"
                  name="roomNumber"
                  value={formData.roomNumber}
                  onChange={handleChange}
                  placeholder="e.g., 101, Lab 3"
                  disabled={isSubmitting}
                />
              </div>
            </div>
            
            {/* Location Field */}
            <div className="form-group">
              <label htmlFor="location">Specific Location (Optional)</label>
              <input
                type="text"
                id="location"
                name="location"
                value={formData.location}
                onChange={handleChange}
                placeholder="e.g., 2nd floor computer lab, front desk"
                disabled={isSubmitting}
              />
              <div className="form-help">
                Help our technicians find you quickly
              </div>
            </div>
            
            {/* Department Field (Auto-filled from user profile) */}
            <div className="form-group">
              <label htmlFor="department">Department</label>
              <input
                type="text"
                id="department"
                name="department"
                value={formData.department}
                onChange={handleChange}
                disabled={true}
                className="disabled-field"
              />
              <div className="form-help">
                This is automatically set from your profile
              </div>
            </div>
            
            {/* Form Actions */}
            <div className="form-actions">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => navigate('/dashboard')}
                disabled={isSubmitting}
              >
                Cancel
              </button>
              <button
                type="submit"
                className="btn btn-primary"
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  <>
                    <span className="spinner"></span>
                    Creating Ticket...
                  </>
                ) : (
                  'Create Ticket'
                )}
              </button>
            </div>
          </form>
        </div>
        
        <div className="create-ticket-footer">
          <Link to="/dashboard" className="back-link">
            ‚Üê Back to Dashboard
          </Link>
          <div className="support-info">
            Need help? Contact IT Support: 
            <a href="mailto:support@bugemauniv.ac.ug">support@bugemauniv.ac.ug</a> 
            | üìû 0784845785
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreateTicket;
