// src/App.js - PRODUCTION READY
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';

// Styles
import './App.css';

// Create query client
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: 1,
      refetchOnWindowFocus: false,
    },
  },
});

// Configure axios
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5002';

// Mock Components (will be replaced with real ones)
const Login = () => (
  <div style={styles.page}>
    <div style={styles.card}>
      <h2>üîê Login</h2>
      <form style={styles.form}>
        <input type="email" placeholder="Email" style={styles.input} />
        <input type="password" placeholder="Password" style={styles.input} />
        <button type="submit" style={styles.button}>Login</button>
      </form>
      <div style={styles.links}>
        <a href="/register" style={styles.link}>Register</a>
        <a href="/forgot-password" style={styles.link}>Forgot Password?</a>
      </div>
    </div>
  </div>
);

const Register = () => (
  <div style={styles.page}>
    <div style={styles.card}>
      <h2>üìù Register</h2>
      <form style={styles.form}>
        <input type="text" placeholder="Full Name" style={styles.input} />
        <input type="email" placeholder="Email" style={styles.input} />
        <input type="password" placeholder="Password" style={styles.input} />
        <select style={styles.input}>
          <option value="">Select Role</option>
          <option value="student">Student</option>
          <option value="staff">Staff</option>
          <option value="technician">Technician</option>
          <option value="admin">Admin</option>
        </select>
        <button type="submit" style={styles.button}>Register</button>
      </form>
      <a href="/login" style={styles.link}>Already have an account? Login</a>
    </div>
  </div>
);

const Dashboard = ({ role }) => (
  <div style={styles.page}>
    <nav style={styles.navbar}>
      <h2>Bugema IT Support System</h2>
      <div style={styles.navLinks}>
        <a href="/dashboard" style={styles.navLink}>Dashboard</a>
        <a href="/tickets" style={styles.navLink}>Tickets</a>
        <a href="/profile" style={styles.navLink}>Profile</a>
        <button style={styles.logoutButton}>Logout</button>
      </div>
    </nav>
    <div style={styles.dashboardContent}>
      <h1>Welcome, {role}!</h1>
      <div style={styles.stats}>
        <div style={styles.statCard}>
          <h3>Open Tickets</h3>
          <p style={styles.statNumber}>12</p>
        </div>
        <div style={styles.statCard}>
          <h3>In Progress</h3>
          <p style={styles.statNumber}>5</p>
        </div>
        <div style={styles.statCard}>
          <h3>Resolved</h3>
          <p style={styles.statNumber}>48</p>
        </div>
      </div>
    </div>
  </div>
);

const Tickets = () => (
  <div style={styles.page}>
    <nav style={styles.navbar}>...</nav>
    <div style={styles.content}>
      <h1>üé´ Support Tickets</h1>
      <button style={styles.primaryButton}>+ Create New Ticket</button>
      <div style={styles.tableContainer}>
        <table style={styles.table}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Status</th>
              <th>Priority</th>
              <th>Created</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>#001</td>
              <td>Network Issue</td>
              <td><span style={styles.statusOpen}>Open</span></td>
              <td>High</td>
              <td>2 hours ago</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  </div>
);

const NotFound = () => (
  <div style={{...styles.page, alignItems: 'center', justifyContent: 'center'}}>
    <div style={{textAlign: 'center'}}>
      <h1 style={{fontSize: '72px', margin: 0}}>404</h1>
      <h2>Page Not Found</h2>
      <a href="/" style={styles.button}>Go Home</a>
    </div>
  </div>
);

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    // Check auth status
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
      setUserRole('admin'); // Default for now
    }
  }, []);

  return (
    <QueryClientProvider client={queryClient}>
      <Router>
        <div className="App">
          <Routes>
            <Route path="/" element={<Navigate to="/login" replace />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/forgot-password" element={
              <div style={styles.page}>
                <div style={styles.card}>
                  <h2>üîë Forgot Password</h2>
                  <form style={styles.form}>
                    <input type="email" placeholder="Enter your email" style={styles.input} />
                    <button type="submit" style={styles.button}>Reset Password</button>
                  </form>
                  <a href="/login" style={styles.link}>Back to Login</a>
                </div>
              </div>
            } />
            
            {/* Protected Routes */}
            {isAuthenticated ? (
              <>
                <Route path="/dashboard" element={<Dashboard role={userRole} />} />
                <Route path="/tickets" element={<Tickets />} />
                <Route path="/profile" element={
                  <div style={styles.page}>
                    <nav style={styles.navbar}>...</nav>
                    <div style={styles.content}>
                      <h1>üë§ Profile</h1>
                      {/* Profile content */}
                    </div>
                  </div>
                } />
              </>
            ) : (
              <Route path="*" element={<Navigate to="/login" replace />} />
            )}
            
            <Route path="*" element={<NotFound />} />
          </Routes>
          
          <ToastContainer
            position="top-right"
            autoClose={5000}
            hideProgressBar={false}
            newestOnTop
            closeOnClick
            rtl={false}
            pauseOnFocusLoss
            draggable
            pauseOnHover
            theme="light"
          />
        </div>
      </Router>
    </QueryClientProvider>
  );
}

const styles = {
  page: {
    minHeight: '100vh',
    backgroundColor: '#f5f5f5',
    display: 'flex',
    flexDirection: 'column',
  },
  card: {
    maxWidth: '400px',
    margin: 'auto',
    padding: '40px',
    backgroundColor: 'white',
    borderRadius: '12px',
    boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '16px',
  },
  input: {
    padding: '12px 16px',
    fontSize: '16px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    outline: 'none',
    transition: 'border-color 0.3s',
  },
  button: {
    padding: '12px',
    backgroundColor: '#1890ff',
    color: 'white',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    cursor: 'pointer',
    fontWeight: '500',
    transition: 'background-color 0.3s',
  },
  links: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '20px',
  },
  link: {
    color: '#1890ff',
    textDecoration: 'none',
    fontSize: '14px',
  },
  navbar: {
    backgroundColor: '#001529',
    color: 'white',
    padding: '0 24px',
    height: '64px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  navLinks: {
    display: 'flex',
    gap: '24px',
    alignItems: 'center',
  },
  navLink: {
    color: 'white',
    textDecoration: 'none',
    padding: '8px 16px',
    borderRadius: '4px',
    transition: 'background-color 0.3s',
  },
  logoutButton: {
    backgroundColor: '#ff4d4f',
    color: 'white',
    border: 'none',
    padding: '8px 16px',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  dashboardContent: {
    padding: '24px',
    flex: 1,
  },
  stats: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
    gap: '24px',
    marginTop: '24px',
  },
  statCard: {
    backgroundColor: 'white',
    padding: '24px',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    textAlign: 'center',
  },
  statNumber: {
    fontSize: '36px',
    fontWeight: 'bold',
    margin: '8px 0',
    color: '#1890ff',
  },
  content: {
    padding: '24px',
    flex: 1,
  },
  primaryButton: {
    backgroundColor: '#52c41a',
    color: 'white',
    border: 'none',
    padding: '12px 24px',
    borderRadius: '6px',
    cursor: 'pointer',
    fontSize: '16px',
    marginBottom: '24px',
  },
  tableContainer: {
    backgroundColor: 'white',
    borderRadius: '8px',
    overflow: 'hidden',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  statusOpen: {
    backgroundColor: '#fff7e6',
    color: '#fa8c16',
    padding: '4px 8px',
    borderRadius: '4px',
    fontSize: '12px',
    fontWeight: '500',
  },
};

export default App;
