import React, { useState, useEffect } from 'react';
import { useUsers, useCreateUser, useUpdateUser, useDeleteUser } from '../../hooks/useAdminData';
import CreateUserModal from './CreateUserModal'; // âœ… FIXED IMPORT

const UserManagement = () => {
  const [filters, setFilters] = useState({});
  const [showCreateModal, setShowCreateModal] = useState(false);
  
  const { data: usersData, isLoading, error, refetch } = useUsers(filters);
  const createUserMutation = useCreateUser();
  const updateUserMutation = useUpdateUser();
  const deleteUserMutation = useDeleteUser();

  // Debug: Log the data we're receiving
  useEffect(() => {
    console.log('Users Data:', usersData);
    console.log('Loading:', isLoading);
    console.log('Error:', error);
  }, [usersData, isLoading, error]);

  const users = usersData?.users || [];
  const pagination = usersData || {};

  const handleCreateUser = async (userData) => {
    console.log('Creating user:', userData);
    try {
      const result = await createUserMutation.mutateAsync(userData);
      console.log('User created successfully:', result);
      setShowCreateModal(false);
      refetch();
    } catch (error) {
      console.error('Failed to create user:', error);
      alert('Error creating user: ' + error.message);
    }
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await deleteUserMutation.mutateAsync(userId);
        refetch();
        alert('User deleted successfully');
      } catch (error) {
        console.error('Failed to delete user:', error);
        alert('Error deleting user: ' + error.message);
      }
    }
  };

  if (isLoading) return <div className="loading">Loading users...</div>;
  if (error) return <div className="error">Error loading users: {error.message}</div>;

  return (
    <div className="user-management">
      <div className="section-header">
        <h2>User Management</h2>
        <div className="header-actions">
          <button 
            className="btn-primary"
            onClick={() => setShowCreateModal(true)}
            disabled={createUserMutation.isLoading}
          >
            {createUserMutation.isLoading ? 'Creating...' : 'Create New User'}
          </button>
          <button 
            className="btn-secondary"
            onClick={refetch}
            disabled={isLoading}
          >
            Refresh
          </button>
        </div>
      </div>

      {/* Debug info */}
      <div style={{ background: '#f8f9fa', padding: '10px', borderRadius: '5px', marginBottom: '15px', fontSize: '14px' }}>
        <strong>Debug Info:</strong> Found {users.length} users | 
        Total: {pagination.total || 0} | 
        Pages: {pagination.totalPages || 1}
      </div>

      {/* Users table */}
      <div className="users-table">
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Email</th>
              <th>Role</th>
              <th>Department</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.length === 0 ? (
              <tr>
                <td colSpan="6" style={{ textAlign: 'center', padding: '40px', color: '#6c757d' }}>
                  <div style={{ fontSize: '3rem', marginBottom: '10px' }}>ðŸ‘¥</div>
                  <h3>No Users Found</h3>
                  <p>Create your first user to get started</p>
                </td>
              </tr>
            ) : (
              users.map(user => (
                <tr key={user._id}>
                  <td>
                    <strong>{user.username}</strong>
                    <div style={{ fontSize: '0.8rem', color: '#6c757d' }}>
                      ID: {user._id}
                    </div>
                  </td>
                  <td>{user.email}</td>
                  <td>
                    <span className={`role-badge ${user.role}`}>
                      {user.role}
                    </span>
                  </td>
                  <td>{user.department}</td>
                  <td>
                    <span className={`status-badge ${user.isActive ? 'active' : 'inactive'}`}>
                      {user.isActive ? 'Active' : 'Inactive'}
                    </span>
                    {user.isVerified && <div style={{ fontSize: '0.7rem', color: '#28a745' }}>âœ“ Verified</div>}
                  </td>
                  <td>
                    <button className="btn-sm">Edit</button>
                    <button 
                      className="btn-sm danger"
                      onClick={() => handleDeleteUser(user._id)}
                      disabled={deleteUserMutation.isLoading}
                    >
                      {deleteUserMutation.isLoading ? 'Deleting...' : 'Delete'}
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Create User Modal */}
      {showCreateModal && (
        <CreateUserModal 
          onSubmit={handleCreateUser}
          onClose={() => setShowCreateModal(false)}
          loading={createUserMutation.isLoading}
        />
      )}
    </div>
  );
};

export default UserManagement;
