import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import Navbar from '../components/Navbar';
import CommentSection from '../components/CommentSection';
import ActivityTimeline from '../components/ActivityTimeline';
import AttachmentManager from '../components/AttachmentManager';
import TicketActions from '../components/TicketActions';
import ticketService from '../services/ticketService';
import userService from '../services/userService';
import '../styles/TicketDetail.css';

const TicketDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  
  const [ticket, setTicket] = useState(null);
  const [comments, setComments] = useState([]);
  const [activities, setActivities] = useState([]);
  const [attachments, setAttachments] = useState([]);
  const [technicians, setTechnicians] = useState([]);
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  
  const [updateData, setUpdateData] = useState({
    status: '',
    assignedTo: '',
    urgency: '',
    issueType: ''
  });

  // Fetch all ticket data
  const fetchTicketData = useCallback(async () => {
    try {
      setError('');
      const [ticketData, commentsData, activitiesData] = await Promise.all([
        ticketService.getTicket(id),
        ticketService.getComments(id),
        ticketService.getActivities(id)
      ]);

      setTicket(ticketData);
      setComments(commentsData);
      setActivities(activitiesData);
      setUpdateData({
        status: ticketData.status,
        assignedTo: ticketData.assignedTo?._id || '',
        urgency: ticketData.urgency,
        issueType: ticketData.issueType
      });
    } catch (error) {
      setError('Failed to load ticket data. Please try again.');
      console.error('Error fetching ticket data:', error);
    } finally {
      setLoading(false);
    }
  }, [id]);

  const fetchTechnicians = async () => {
    try {
      const response = await userService.getTechnicians();
      setTechnicians(response);
    } catch (error) {
      console.error('Error fetching technicians:', error);
    }
  };

  useEffect(() => {
    fetchTicketData();
    if (user.role === 'technician' || user.role === 'admin') {
      fetchTechnicians();
    }
  }, [fetchTicketData, user.role]);

  // Auto-hide success messages
  useEffect(() => {
    if (success) {
      const timer = setTimeout(() => setSuccess(''), 3000);
      return () => clearTimeout(timer);
    }
  }, [success]);

  // Enhanced update handlers with better error handling
  const handleTicketUpdate = async (updates, successMessage) => {
    setUpdating(true);
    try {
      await ticketService.updateTicket(id, updates);
      await fetchTicketData();
      setSuccess(successMessage);
      setError('');
    } catch (error) {
      setError('Failed to update ticket. Please try again.');
      console.error('Error updating ticket:', error);
    } finally {
      setUpdating(false);
    }
  };

  const handleStatusUpdate = async () => {
    if (!updateData.status || updateData.status === ticket.status) return;
    await handleTicketUpdate(
      { status: updateData.status }, 
      'Status updated successfully'
    );
  };

  const handleAssignment = async () => {
    await handleTicketUpdate(
      { assignedTo: updateData.assignedTo || null },
      updateData.assignedTo ? 'Ticket assigned successfully' : 'Ticket unassigned successfully'
    );
  };

  const handleUrgencyUpdate = async () => {
    if (updateData.urgency === ticket.urgency) return;
    await handleTicketUpdate(
      { urgency: updateData.urgency },
      'Urgency level updated successfully'
    );
  };

  const handleIssueTypeUpdate = async () => {
    if (updateData.issueType === ticket.issueType) return;
    await handleTicketUpdate(
      { issueType: updateData.issueType },
      'Issue type updated successfully'
    );
  };

  // Quick actions
  const handleQuickAction = async (action, data = {}) => {
    await handleTicketUpdate(
      { status: action, ...data },
      `Ticket ${action.replace('-', ' ')} successfully`
    );
  };

  // Comment handling
  const handleCommentAdded = useCallback(() => {
    fetchTicketData();
  }, [fetchTicketData]);

  // Attachment handling
  const handleAttachmentUpload = async (file) => {
    try {
      await ticketService.uploadAttachment(id, file);
      await fetchTicketData();
      setSuccess('File uploaded successfully');
    } catch (error) {
      setError('Failed to upload file. Please try again.');
    }
  };

  const handleAttachmentDelete = async (fileId) => {
    try {
      await ticketService.deleteAttachment(id, fileId);
      await fetchTicketData();
      setSuccess('File deleted successfully');
    } catch (error) {
      setError('Failed to delete file. Please try again.');
    }
  };

  // Calculate SLA status
  const getSLAStatus = () => {
    if (!ticket) return null;
    
    const created = new Date(ticket.createdAt);
    const now = new Date();
    const hoursDiff = (now - created) / (1000 * 60 * 60);
    
    const SLATimes = {
      critical: 4,
      high: 8,
      medium: 24,
      low: 72
    };
    
    const targetTime = SLATimes[ticket.urgency] || 72;
    const percent = Math.min((hoursDiff / targetTime) * 100, 100);
    const hoursRemaining = Math.max(targetTime - hoursDiff, 0);
    
    return {
      percent,
      overdue: hoursDiff > targetTime,
      hoursRemaining: hoursRemaining.toFixed(1),
      targetTime
    };
  };

  const slaStatus = ticket ? getSLAStatus() : null;

  if (loading) {
    return (
      <>
        <Navbar />
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading ticket details...</p>
        </div>
      </>
    );
  }

  if (!ticket) {
    return (
      <>
        <Navbar />
        <div className="error-container">
          <div className="error-icon">⚠️</div>
          <h2>Ticket Not Found</h2>
          <p>The ticket you're looking for doesn't exist or you don't have permission to view it.</p>
          <button onClick={() => navigate('/tickets')} className="btn-primary">
            Back to Tickets
          </button>
        </div>
      </>
    );
  }

  return (
    <>
      <Navbar />
      <div className="ticket-detail">
        {/* Success/Error Messages */}
        {error && (
          <div className="message-banner error">
            <span>{error}</span>
            <button onClick={() => setError('')}>×</button>
          </div>
        )}
        {success && (
          <div className="message-banner success">
            <span>{success}</span>
            <button onClick={() => setSuccess('')}>×</button>
          </div>
        )}

        {/* Header Section */}
        <div className="ticket-detail-header">
          <div className="header-main">
            <div className="breadcrumb">
              <button onClick={() => navigate('/tickets')} className="breadcrumb-link">
                Tickets
              </button>
              <span className="breadcrumb-separator">/</span>
              <span className="breadcrumb-current">#{ticket.ticketId}</span>
            </div>
            
            <div className="title-section">
              <h1>{ticket.title}</h1>
              <div className="ticket-meta-badges">
                <span className={`status-badge status-${ticket.status}`}>
                  {ticket.status.replace('-', ' ')}
                </span>
                <span className={`urgency-badge urgency-${ticket.urgency}`}>
                  {ticket.urgency}
                </span>
                <span className="ticket-id">#{ticket.ticketId}</span>
              </div>
            </div>

            {/* SLA Indicator */}
            {slaStatus && (
              <div className="sla-indicator">
                <div className="sla-info">
                  <span className="sla-label">
                    {slaStatus.overdue ? 'SLA BREACHED' : 'SLA Progress'}
                  </span>
                  <span className="sla-time">
                    {slaStatus.overdue ? 'Overdue' : `${slaStatus.hoursRemaining}h left`}
                  </span>
                </div>
                <div className="sla-progress-bar">
                  <div 
                    className={`sla-progress ${slaStatus.overdue ? 'overdue' : ''}`}
                    style={{ width: `${slaStatus.percent}%` }}
                  ></div>
                </div>
              </div>
            )}
          </div>

          <div className="header-actions">
            <TicketActions 
              ticket={ticket}
              user={user}
              onQuickAction={handleQuickAction}
              updating={updating}
            />
            <button onClick={() => navigate('/tickets')} className="btn-secondary">
              ← Back to Tickets
            </button>
          </div>
        </div>

        <div className="ticket-detail-content">
          {/* Left Column - Main Content */}
          <div className="ticket-left-column">
            {/* Ticket Information Card */}
            <div className="ticket-card">
              <div className="card-header">
                <h3>Ticket Information</h3>
              </div>
              <div className="card-body">
                <div className="description-section">
                  <h4>Description</h4>
                  <p className="ticket-description">{ticket.description}</p>
                </div>

                <div className="info-grid">
                  <div className="info-item">
                    <label>Issue Type</label>
                    <span className="info-value">{ticket.issueType}</span>
                  </div>
                  <div className="info-item">
                    <label>Created By</label>
                    <span className="info-value">{ticket.createdBy?.username}</span>
                  </div>
                  <div className="info-item">
                    <label>Created Date</label>
                    <span className="info-value" title={new Date(ticket.createdAt).toLocaleString()}>
                      {new Date(ticket.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                  <div className="info-item">
                    <label>Last Updated</label>
                    <span className="info-value" title={new Date(ticket.updatedAt).toLocaleString()}>
                      {new Date(ticket.updatedAt).toLocaleDateString()}
                    </span>
                  </div>
                  {ticket.assignedTo && (
                    <div className="info-item">
                      <label>Assigned To</label>
                      <div className="user-assignment">
                        <div className="user-avatar">
                          {ticket.assignedTo.username?.charAt(0).toUpperCase()}
                        </div>
                        <span className="info-value">{ticket.assignedTo.username}</span>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Attachments Section */}
            <AttachmentManager
              attachments={attachments}
              onUpload={handleAttachmentUpload}
              onDelete={handleAttachmentDelete}
              ticketId={id}
              userRole={user.role}
            />

            {/* Comments Section */}
            <CommentSection
              comments={comments}
              ticketId={id}
              user={user}
              onCommentAdded={handleCommentAdded}
            />
          </div>

          {/* Right Column - Management & Activity */}
          <div className="ticket-right-column">
            {/* Management Panel for Technicians/Admins */}
            {(user.role === 'technician' || user.role === 'admin') && (
              <div className="management-panel">
                <div className="panel-header">
                  <h3>Ticket Management</h3>
                </div>

                {/* Quick Status Actions */}
                <div className="control-section">
                  <h4>Quick Actions</h4>
                  <div className="quick-actions">
                    {ticket.status !== 'in-progress' && (
                      <button
                        onClick={() => handleQuickAction('in-progress')}
                        disabled={updating}
                        className="btn-action progress"
                      >
                        Start Progress
                      </button>
                    )}
                    {ticket.status !== 'resolved' && (
                      <button
                        onClick={() => handleQuickAction('resolved')}
                        disabled={updating}
                        className="btn-action resolve"
                      >
                        Mark Resolved
                      </button>
                    )}
                    {ticket.status !== 'closed' && ticket.status === 'resolved' && (
                      <button
                        onClick={() => handleQuickAction('closed')}
                        disabled={updating}
                        className="btn-action close"
                      >
                        Close Ticket
                      </button>
                    )}
                  </div>
                </div>

                {/* Status Update */}
                <div className="control-section">
                  <h4>Update Status</h4>
                  <div className="control-group">
                    <select
                      value={updateData.status}
                      onChange={(e) => setUpdateData(prev => ({ ...prev, status: e.target.value }))}
                      className="form-select"
                    >
                      <option value="open">Open</option>
                      <option value="in-progress">In Progress</option>
                      <option value="awaiting-user">Awaiting User</option>
                      <option value="resolved">Resolved</option>
                      <option value="closed">Closed</option>
                    </select>
                    <button 
                      onClick={handleStatusUpdate}
                      disabled={updating || updateData.status === ticket.status}
                      className="btn-primary"
                    >
                      {updating ? 'Updating...' : 'Update Status'}
                    </button>
                  </div>
                </div>

                {/* Assignment */}
                <div className="control-section">
                  <h4>Assignment</h4>
                  <div className="control-group">
                    <select
                      value={updateData.assignedTo}
                      onChange={(e) => setUpdateData(prev => ({ ...prev, assignedTo: e.target.value }))}
                      className="form-select"
                    >
                      <option value="">Unassigned</option>
                      {technicians.map(tech => (
                        <option key={tech._id} value={tech._id}>
                          {tech.username}
                        </option>
                      ))}
                    </select>
                    <button 
                      onClick={handleAssignment}
                      disabled={updating}
                      className="btn-primary"
                    >
                      {updating ? 'Assigning...' : 'Assign'}
                    </button>
                  </div>
                </div>

                {/* Additional Controls */}
                <div className="control-section">
                  <h4>Ticket Properties</h4>
                  
                  {/* Urgency Update */}
                  <div className="control-group">
                    <label>Urgency Level</label>
                    <select
                      value={updateData.urgency}
                      onChange={(e) => setUpdateData(prev => ({ ...prev, urgency: e.target.value }))}
                      className="form-select"
                    >
                      <option value="low">Low</option>
                      <option value="medium">Medium</option>
                      <option value="high">High</option>
                      <option value="critical">Critical</option>
                    </select>
                    <button 
                      onClick={handleUrgencyUpdate}
                      disabled={updating || updateData.urgency === ticket.urgency}
                      className="btn-primary"
                    >
                      Update
                    </button>
                  </div>

                  {/* Issue Type Update */}
                  <div className="control-group">
                    <label>Issue Type</label>
                    <select
                      value={updateData.issueType}
                      onChange={(e) => setUpdateData(prev => ({ ...prev, issueType: e.target.value }))}
                      className="form-select"
                    >
                      <option value="hardware">Hardware</option>
                      <option value="software">Software</option>
                      <option value="network">Network</option>
                      <option value="account">Account</option>
                      <option value="security">Security</option>
                      <option value="other">Other</option>
                    </select>
                    <button 
                      onClick={handleIssueTypeUpdate}
                      disabled={updating || updateData.issueType === ticket.issueType}
                      className="btn-primary"
                    >
                      Update
                    </button>
                  </div>
                </div>
              </div>
            )}

            {/* Activity Timeline */}
            <ActivityTimeline activities={activities} />
          </div>
        </div>
