import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import Navbar from '../components/Navbar';
import TicketCard from '../components/TicketCard';
import ticketService from '../services/ticketService';
import '../styles/Tickets.css';

// Debounce utility for search optimization
const debounce = (func, wait) => {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
};

const Tickets = () => {
  const { user } = useAuth();
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedTickets, setSelectedTickets] = useState(new Set());
  const [bulkAction, setBulkAction] = useState('');
  
  const [filters, setFilters] = useState({
    status: '',
    urgency: '',
    issueType: '',
    assignedTo: '',
    dateFrom: '',
    dateTo: '',
    search: '',
    page: 1,
    limit: 10,
    sortBy: 'createdAt',
    sortOrder: 'desc'
  });
  
  const [pagination, setPagination] = useState({});

  // Debounced search function
  const debouncedSearch = useCallback(
    debounce((value) => {
      setFilters(prev => ({
        ...prev,
        search: value,
        page: 1
      }));
    }, 300),
    []
  );

  const handleSearch = (e) => {
    const value = e.target.value;
    debouncedSearch(value);
  };

  const fetchTickets = async () => {
    try {
      setError('');
      setLoading(true);
      const response = await ticketService.getTickets(filters);
      setTickets(response.tickets);
      setPagination({
        totalPages: response.totalPages,
        currentPage: response.currentPage,
        total: response.total
      });
    } catch (error) {
      setError('Failed to load tickets. Please try again.');
      console.error('Error fetching tickets:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTickets();
  }, [filters]);

  // Sync filters with URL (professional touch)
  useEffect(() => {
    const params = new URLSearchParams();
    Object.entries(filters).forEach(([key, value]) => {
      if (value) params.set(key, value);
    });
    window.history.replaceState({}, '', `${window.location.pathname}?${params}`);
  }, [filters]);

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: 1 // Reset to first page when filters change
    }));
  };

  const handlePageChange = (newPage) => {
    setFilters(prev => ({ ...prev, page: newPage }));
  };

  const clearFilters = () => {
    setFilters({
      status: '',
      urgency: '',
      issueType: '',
      assignedTo: '',
      dateFrom: '',
      dateTo: '',
      search: '',
      page: 1,
      limit: 10,
      sortBy: 'createdAt',
      sortOrder: 'desc'
    });
    setSelectedTickets(new Set());
  };

  // Bulk operations
  const handleBulkAction = async () => {
    if (!bulkAction || selectedTickets.size === 0) return;
    
    try {
      await ticketService.bulkUpdateStatus(Array.from(selectedTickets), bulkAction);
      await fetchTickets(); // Refresh the list
      setSelectedTickets(new Set());
      setBulkAction('');
    } catch (error) {
      setError('Bulk update failed. Please try again.');
      console.error('Bulk update error:', error);
    }
  };

  const handleTicketSelect = (ticketId) => {
    setSelectedTickets(prev => {
      const newSelection = new Set(prev);
      if (newSelection.has(ticketId)) {
        newSelection.delete(ticketId);
      } else {
        newSelection.add(ticketId);
      }
      return newSelection;
    });
  };

  const selectAllTickets = () => {
    if (selectedTickets.size === tickets.length) {
      setSelectedTickets(new Set());
    } else {
      setSelectedTickets(new Set(tickets.map(ticket => ticket._id)));
    }
  };

  const exportTickets = async () => {
    try {
      const response = await ticketService.exportTickets(filters);
      const blob = new Blob([response], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `tickets-${new Date().toISOString().split('T')[0]}.csv`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      setError('Export failed. Please try again.');
      console.error('Export error:', error);
    }
  };

  if (loading) {
    return (
      <>
        <Navbar />
        <div className="loading">Loading tickets...</div>
      </>
    );
  }

  return (
    <>
      <Navbar />
      <div className="tickets-page">
        <div className="tickets-header">
          <h1>{user.role === 'staff' ? 'My Tickets' : 'All Tickets'}</h1>
          <div className="header-actions">
            {user.role === 'staff' && (
              <a href="/create-ticket" className="btn-primary">
                Create New Ticket
              </a>
            )}
            <button onClick={exportTickets} className="btn-secondary">
              Export CSV
            </button>
          </div>
        </div>

        {/* Error Display */}
        {error && (
          <div className="error-message">
            <span>{error}</span>
            <button onClick={() => setError('')}>Ã—</button>
          </div>
        )}

        {/* Filters Section */}
        <div className="filters-section">
          <div className="search-box">
            <input
              type="text"
              placeholder="Search tickets by title, description, or ID..."
              onChange={handleSearch}
            />
          </div>

          <div className="filter-grid">
            <select
              value={filters.status}
              onChange={(e) => handleFilterChange('status', e.target.value)}
            >
              <option value="">All Status</option>
              <option value="open">Open</option>
              <option value="in-progress">In Progress</option>
              <option value="awaiting-user">Awaiting User</option>
              <option value="resolved">Resolved</option>
              <option value="closed">Closed</option>
            </select>

            <select
              value={filters.urgency}
              onChange={(e) => handleFilterChange('urgency', e.target.value)}
            >
              <option value="">All Urgency</option>
              <option value="low">Low</option>
              <option value="medium">Medium</option>
              <option value="high">High</option>
              <option value="critical">Critical</option>
            </select>

            <select
              value={filters.issueType}
              onChange={(e) => handleFilterChange('issueType', e.target.value)}
            >
              <option value="">All Types</option>
              <option value="hardware">Hardware</option>
              <option value="software">Software</option>
              <option value="network">Network</option>
              <option value="account">Account</option>
              <option value="security">Security</option>
              <option value="other">Other</option>
            </select>

            <input
              type="date"
              value={filters.dateFrom}
              onChange={(e) => handleFilterChange('dateFrom', e.target.value)}
              placeholder="From Date"
            />
            
            <input
              type="date"
              value={filters.dateTo}
              onChange={(e) => handleFilterChange('dateTo', e.target.value)}
              placeholder="To Date"
            />

            <select
              value={filters.sortBy}
              onChange={(e) => handleFilterChange('sortBy', e.target.value)}
            >
              <option value="createdAt">Sort by Created</option>
              <option value="updatedAt">Sort by Updated</option>
              <option value="urgency">Sort by Urgency</option>
              <option value="title">Sort by Title</option>
            </select>

            <button onClick={clearFilters} className="btn-secondary">
              Clear Filters
            </button>
          </div>
        </div>

        {/* Bulk Actions */}
        {selectedTickets.size > 0 && (
          <div className="bulk-actions">
            <div className="bulk-info">
              <span>{selectedTickets.size} ticket(s) selected</span>
            </div>
            <div className="bulk-controls">
              <select
                value={bulkAction}
                onChange={(e) => setBulkAction(e.target.value)}
              >
                <option value="">Bulk Actions</option>
                <option value="in-progress">Mark In Progress</option>
                <option value="resolved">Mark Resolved</option>
                <option value="closed">Mark Closed</option>
              </select>
              <button onClick={handleBulkAction} className="btn-primary">
                Apply
              </button>
              <button onClick={() => setSelectedTickets(new Set())} className="btn-secondary">
                Clear Selection
              </button>
            </div>
          </div>
        )}

        {/* Tickets List */}
        <div className="tickets-container">
          {tickets.length === 0 ? (
            <div className="no-tickets">
              <p>No tickets found matching your criteria.</p>
              {user.role === 'staff' && (
                <a href="/create-ticket" className="btn-primary">
                  Create Your First Ticket
                </a>
              )}
            </div>
          ) : (
            <>
              <div className="tickets-stats">
                <div className="stats-header">
                  <p>Showing {tickets.length} of {pagination.total} tickets</p>
                  <div className="select-all">
                    <input
                      type="checkbox"
                      checked={selectedTickets.size === tickets.length && tickets.length > 0}
                      onChange={selectAllTickets}
                    />
                    <span>Select All</span>
                  </div>
                </div>
              </div>
              
              <div className="tickets-grid">
                {tickets.map(ticket => (
                  <div key={ticket._id} className="ticket-card-wrapper">
                    <input
                      type="checkbox"
                      checked={selectedTickets.has(ticket._id)}
                      onChange={() => handleTicketSelect(ticket._id)}
                      className="ticket-select"
                    />
                    <TicketCard ticket={ticket} />
                  </div>
                ))}
              </div>

              {/* Pagination */}
              {pagination.totalPages > 1 && (
                <div className="pagination">
                  <button
                    onClick={() => handlePageChange(filters.page - 1)}
                    disabled={filters.page === 1}
                    className="pagination-btn"
                  >
                    Previous
                  </button>
                  
                  <span className="pagination-info">
                    Page {filters.page} of {pagination.totalPages}
                  </span>
                  
                  <button
                    onClick={() => handlePageChange(filters.page + 1)}
                    disabled={filters.page === pagination.totalPages}
                    className="pagination-btn"
                  >
                    Next
                  </button>
                </div>
              )}
            </>
          )}
        </div>
      </div>
    </>
  );
};

export default Tickets;
