import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import Navbar from '../components/Navbar';
import '../styles/Auth.css';

/**
 * Production-Grade Enterprise Registration Component
 * 
 * Features:
 * - Enhanced security validation
 * - Real-time password strength analysis
 * - Input sanitization and validation
 * - Role-based dynamic form fields
 * - Form progress tracking
 * - Terms of service acceptance
 * - Email domain validation (configurable)
 * - Phone number formatting and validation
 * - Student ID validation
 * - Department auto-suggestion
 * - Security compliance checks
 * - Audit logging integration
 * - Accessibility compliance
 * - Responsive design
 * - Loading states with progress indicators
 * 
 * @version 2.0.0
 * @author IT Support System
 */

const Register = () => {
  // Main form state
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: 'student',
    department: '',
    studentId: '',
    phone: '',
    agreeToTerms: false,
    receiveNotifications: true
  });

  // Component state
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [validationErrors, setValidationErrors] = useState({});
  const [passwordStrength, setPasswordStrength] = useState(0);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [formProgress, setFormProgress] = useState(0);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [suggestedDepartments, setSuggestedDepartments] = useState([]);
  const [registrationStep, setRegistrationStep] = useState(1);
  const [isPersonalEmail, setIsPersonalEmail] = useState(false);

  const nameRef = useRef(null);
  const { register, logSecurityEvent } = useAuth();
  const navigate = useNavigate();

  // Role options with enhanced descriptions and permissions
  const roleOptions = [
    {
      value: 'student',
      label: 'Student',
      description: 'Access academic resources and submit support tickets',
      icon: 'ðŸŽ“',
      permissions: ['create_ticket', 'view_own_tickets', 'access_knowledge_base'],
      requiresApproval: false
    },
    {
      value: 'staff',
      label: 'Staff/Faculty', 
      description: 'University staff and teaching faculty members',
      icon: 'ðŸ‘¨â€ðŸ«',
      permissions: ['create_ticket', 'view_department_tickets', 'access_knowledge_base', 'manage_own_assets'],
      requiresApproval: true
    },
    {
      value: 'technician',
      label: 'IT Technician',
      description: 'Provide technical support and resolve tickets',
      icon: 'ðŸ‘¨â€ðŸ’»',
      permissions: ['view_all_tickets', 'resolve_tickets', 'manage_assets', 'access_admin_tools'],
      requiresApproval: true
    },
    {
      value: 'admin',
      label: 'System Administrator',
      description: 'Full system access and user management',
      icon: 'ðŸ‘¨â€ðŸ’¼',
      permissions: ['all_permissions'],
      requiresApproval: true
    }
  ];

  // Enhanced departments with categories
  const departments = [
    { value: 'computer_science', label: 'Computer Science', category: 'academic' },
    { value: 'engineering', label: 'Engineering', category: 'academic' },
    { value: 'business', label: 'Business School', category: 'academic' },
    { value: 'arts_sciences', label: 'Arts & Sciences', category: 'academic' },
    { value: 'medicine', label: 'School of Medicine', category: 'academic' },
    { value: 'law', label: 'School of Law', category: 'academic' },
    { value: 'education', label: 'School of Education', category: 'academic' },
    { value: 'it_services', label: 'IT Services', category: 'administrative' },
    { value: 'administration', label: 'Administration', category: 'administrative' },
    { value: 'facilities', label: 'Facilities Management', category: 'administrative' },
    { value: 'library', label: 'Library Services', category: 'administrative' },
    { value: 'student_services', label: 'Student Services', category: 'administrative' },
    { value: 'research', label: 'Research Division', category: 'administrative' },
    { value: 'other', label: 'Other Department', category: 'other' }
  ];

  // Email validation config (ALLOW personal emails)
  const universityDomains = [
    'bugemauniv.ac.ug',
    'students.bugemauniv.ac.ug',
    'staff.bugemauniv.ac.ug'
  ];

  // Student ID format patterns (Bugema University format)
  const studentIdPatterns = [
    /^[0-9]{2}\/[A-Z]{3}\/[A-Z]{2}\/[A-Z]\/[0-9]{4}$/, // 22/BIT/BU/R/0010
    /^[0-9]{2}\/[A-Z]{3,5}\/[A-Z]{2}\/[A-Z]\/[0-9]{3,5}$/, // Flexible
    /^[A-Z0-9\/\-_]{6,20}$/ // General pattern
  ];

  // Auto-focus first input on mount
  useEffect(() => {
    if (nameRef.current) {
      nameRef.current.focus();
    }
  }, []);

  // Calculate form progress
  useEffect(() => {
    calculateFormProgress();
  }, [formData]);

  // Calculate password strength
  useEffect(() => {
    if (formData.password) {
      calculatePasswordStrength(formData.password);
    } else {
      setPasswordStrength(0);
    }
  }, [formData.password]);

  // Suggest departments based on role
  useEffect(() => {
    if (formData.role === 'student' || formData.role === 'staff') {
      setSuggestedDepartments(departments.filter(dept => dept.category === 'academic'));
    } else if (formData.role === 'technician' || formData.role === 'admin') {
      setSuggestedDepartments(departments.filter(dept => dept.category === 'administrative'));
    } else {
      setSuggestedDepartments(departments);
    }
  }, [formData.role]);

  /**
   * Validates email format (ALLOWS personal emails)
   */
  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    
    if (!emailRegex.test(email.trim())) {
      return 'Please enter a valid email address';
    }

    // Check if it's a university email (for informational purposes only)
    const isUniversityEmail = universityDomains.some(domain => 
      email.toLowerCase().endsWith(domain.toLowerCase())
    );

    // Update state to show different messages
    setIsPersonalEmail(!isUniversityEmail);

    return null;
  };

  /**
   * Validates phone number format
   */
  const validatePhone = (phone) => {
    if (!phone.trim()) return null;
    
    // Allow various phone formats
    const phoneRegex = /^[\+]?[1-9][\d]{0,15}$/;
    const cleanedPhone = phone.replace(/[\s\-\(\)\.]/g, '');
    
    if (!phoneRegex.test(cleanedPhone)) {
      return 'Please enter a valid phone number';
    }
    
    // Validate Uganda phone numbers specifically
    if (cleanedPhone.startsWith('256') && cleanedPhone.length !== 12) {
      return 'Ugandan phone numbers must be 12 digits with 256 prefix';
    }
    
    return null;
  };

  /**
   * Validates student ID format for Bugema University
   */
  const validateStudentId = (studentId, role) => {
    if (role !== 'student') return null;
    
    if (!studentId || !studentId.trim()) {
      return 'Student ID is required for student accounts';
    }
    
    // Convert to uppercase and trim
    const formattedId = studentId.trim().toUpperCase();
    
    // Check length
    if (formattedId.length < 6 || formattedId.length > 20) {
      return 'Student ID must be 6-20 characters';
    }
    
    // Check format against patterns
    const isValidFormat = studentIdPatterns.some(pattern => pattern.test(formattedId));
    
    if (!isValidFormat) {
      return 'Invalid student ID format. Expected: YY/DEPT/CAMPUS/LEVEL/NUMBER (e.g., 22/BIT/BU/R/0010)';
    }
    
    // Check if it has at least one letter and one number
    if (!/[A-Z]/.test(formattedId) || !/[0-9]/.test(formattedId)) {
      return 'Student ID must contain both letters and numbers';
    }
    
    return null;
  };

  /**
   * Validates username
   */
  const validateUsername = (username) => {
    if (!username.trim()) {
      return 'Full name is required';
    }
    
    if (username.length < 2) {
      return 'Name must be at least 2 characters';
    }
    
    if (username.length > 100) {
      return 'Name must not exceed 100 characters';
    }
    
    // Allow letters, spaces, hyphens, apostrophes, and international characters
    const nameRegex = /^[A-Za-z\s\-\'\u00C0-\u024F\u1E00-\u1EFF]+$/;
    if (!nameRegex.test(username)) {
      return 'Name can only contain letters, spaces, hyphens, and apostrophes';
    }
    
    return null;
  };

  /**
   * Calculates password strength
   */
  const calculatePasswordStrength = (password) => {
    let strength = 0;
    
    // Length check
    if (password.length >= 8) strength += 25;
    if (password.length >= 12) strength += 10;
    
    // Character variety checks
    if (/[A-Z]/.test(password)) strength += 15;
    if (/[a-z]/.test(password)) strength += 15;
    if (/[0-9]/.test(password)) strength += 15;
    if (/[^A-Za-z0-9]/.test(password)) strength += 15;
    
    // Common patterns penalty
    const commonPatterns = [
      '123456', 'password', 'qwerty', '111111',
      new RegExp(formData.username.toLowerCase()),
      new RegExp(formData.email.split('@')[0].toLowerCase())
    ];
    
    commonPatterns.forEach(pattern => {
      if (typeof pattern === 'string' && password.toLowerCase().includes(pattern.toLowerCase())) {
        strength -= 20;
      } else if (pattern instanceof RegExp && pattern.test(password.toLowerCase())) {
        strength -= 20;
      }
    });
    
    // Ensure strength is between 0 and 100
    strength = Math.max(0, Math.min(100, strength));
    setPasswordStrength(strength);
    
    return strength;
  };

  /**
   * Gets password strength label and color
   */
  const getPasswordStrengthInfo = () => {
    if (passwordStrength < 30) return { label: 'Weak', color: '#ef4444', className: 'weak' };
    if (passwordStrength < 60) return { label: 'Fair', color: '#f59e0b', className: 'fair' };
    if (passwordStrength < 80) return { label: 'Good', color: '#10b981', className: 'good' };
    return { label: 'Strong', color: '#059669', className: 'strong' };
  };

  /**
   * Calculates form completion progress
   */
  const calculateFormProgress = () => {
    const requiredFields = {
      student: ['username', 'email', 'password', 'confirmPassword', 'role', 'department', 'studentId', 'agreeToTerms'],
      staff: ['username', 'email', 'password', 'confirmPassword', 'role', 'department', 'agreeToTerms'],
      technician: ['username', 'email', 'password', 'confirmPassword', 'role', 'department', 'agreeToTerms'],
      admin: ['username', 'email', 'password', 'confirmPassword', 'role', 'department', 'agreeToTerms']
    };
    
    const fields = requiredFields[formData.role] || requiredFields.student;
    let completed = 0;
    
    fields.forEach(field => {
      if (field === 'agreeToTerms') {
        if (formData[field]) completed++;
      } else if (formData[field]?.toString().trim()) {
        completed++;
      }
    });
    
    const progress = Math.round((completed / fields.length) * 100);
    setFormProgress(progress);
  };

  /**
   * Handles input changes with validation
   */
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    
    // Sanitize input
    let sanitizedValue = value;
    
    switch (name) {
      case 'username':
        sanitizedValue = value.trim();
        break;
      case 'email':
        sanitizedValue = value.trim().toLowerCase();
        // Re-validate email type when email changes
        if (sanitizedValue) {
          validateEmail(sanitizedValue);
        }
        break;
      case 'phone':
        // Format phone number as user types
        sanitizedValue = formatPhoneNumber(value);
        break;
      case 'studentId':
        sanitizedValue = value.trim().toUpperCase();
        break;
      default:
        sanitizedValue = value;
    }
    
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : sanitizedValue
    }));
    
    // Clear field-specific error when user starts typing
    if (validationErrors[name]) {
      setValidationErrors(prev => ({ ...prev, [name]: '' }));
    }
    
    // Clear general error
    if (error) setError('');
  };

  /**
   * Formats phone number as user types (Uganda specific)
   */
  const formatPhoneNumber = (phone) => {
    // Remove all non-digit characters
    const cleaned = phone.replace(/\D/g, '');
    
    // Format Uganda numbers: +256 XXX XXX XXX
    if (cleaned.startsWith('256')) {
      if (cleaned.length <= 3) return cleaned;
      if (cleaned.length <= 6) return `+${cleaned.slice(0, 3)} ${cleaned.slice(3)}`;
      if (cleaned.length <= 9) return `+${cleaned.slice(0, 3)} ${cleaned.slice(3, 6)} ${cleaned.slice(6)}`;
      return `+${cleaned.slice(0, 3)} ${cleaned.slice(3, 6)} ${cleaned.slice(6, 9)} ${cleaned.slice(9, 12)}`;
    }
    
    // Format other numbers
    if (cleaned.length <= 3) return cleaned;
    if (cleaned.length <= 6) return `(${cleaned.slice(0, 3)}) ${cleaned.slice(3)}`;
    return `(${cleaned.slice(0, 3)}) ${cleaned.slice(3, 6)}-${cleaned.slice(6, 10)}`;
  };

  /**
   * Validates the entire form
   */
  const validateForm = useCallback(() => {
    const errors = {};
    
    // Validate each field
    errors.username = validateUsername(formData.username);
    errors.email = validateEmail(formData.email);
    errors.phone = validatePhone(formData.phone);
    errors.studentId = validateStudentId(formData.studentId, formData.role);
    
    // Password validation
    if (!formData.password) {
      errors.password = 'Password is required';
    } else if (formData.password.length < 8) {
      errors.password = 'Password must be at least 8 characters';
    } else if (passwordStrength < 30) {
      errors.password = 'Please choose a stronger password';
    }
    
    // Confirm password validation
    if (!formData.confirmPassword) {
      errors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = 'Passwords do not match';
    }
    
    // Department validation
    if (!formData.department) {
      errors.department = 'Please select your department';
    }
    
    // Student ID validation for students
    if (formData.role === 'student' && !formData.studentId) {
      errors.studentId = 'Student ID is required for student accounts';
    }
    
    // Terms agreement validation
    if (!formData.agreeToTerms) {
      errors.agreeToTerms = 'You must agree to the Terms of Service';
    }
    
    // Remove null errors
    Object.keys(errors).forEach(key => {
      if (!errors[key]) delete errors[key];
    });
    
    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  }, [formData, passwordStrength]);

  /**
   * Handles form submission with enhanced security
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate form
    if (!validateForm()) {
      setError('Please fix the errors in the form');
      return;
    }
    
    // Specifically validate student ID for students
    if (formData.role === 'student') {
      const studentIdError = validateStudentId(formData.studentId, formData.role);
      if (studentIdError) {
        setError(studentIdError);
        return;
      }
    }
    
    setLoading(true);
    setIsSubmitting(true);
    
    try {
      // Log registration attempt
      logSecurityEvent('registration_attempt', {
        email: formData.email,
        role: formData.role,
        timestamp: new Date().toISOString()
      });
      
      // Prepare data for registration
      const registrationData = {
        username: formData.username.trim(),
        email: formData.email.trim().toLowerCase(),
        password: formData.password,
        role: formData.role,
        department: formData.department,
        phone: formData.phone ? formData.phone.replace(/\D/g, '') : undefined,
        ...(formData.role === 'student' && { 
          studentId: formData.studentId.trim().toUpperCase()
        }),
        metadata: {
          registrationTimestamp: new Date().toISOString(),
          userAgent: navigator.userAgent,
          screenResolution: `${window.screen.width}x${window.screen.height}`,
          timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
          isPersonalEmail: isPersonalEmail
        }
      };
      
      console.log('ðŸ” Registering user:', {
        email: registrationData.email,
        role: registrationData.role,
        department: registrationData.department,
        studentId: registrationData.studentId
      });
      
      const result = await register(registrationData);
      
      if (result.success) {
        // Log successful registration
        logSecurityEvent('registration_success', {
          email: formData.email,
          role: formData.role,
          userId: result.user?._id
        });
        
        // Show success message and redirect
        navigate('/dashboard', {
          state: {
            registrationSuccess: true,
            message: 'Account created successfully!',
            userRole: formData.role
          }
        });
      } else {
        throw new Error(result.error || 'Registration failed');
      }
      
    } catch (err) {
      console.error('âŒ Registration error:', err);
      
      // Enhanced error handling
      let errorMessage = 'Registration failed. Please try again.';
      
      if (err.response) {
        const { status, data } = err.response;
        
        switch (status) {
          case 400:
            errorMessage = data?.message || 'Invalid registration data';
            break;
          case 409:
            errorMessage = 'An account with this email already exists';
            break;
          case 422:
            errorMessage = data?.message || 'Validation failed';
            break;
          case 429:
            errorMessage = 'Too many registration attempts. Please try again later.';
            break;
          case 500:
            errorMessage = 'Server error. Please try again later.';
            break;
          default:
            errorMessage = data?.message || errorMessage;
        }
      } else if (err.code === 'ECONNABORTED') {
        errorMessage = 'Request timeout. Please check your connection.';
      } else if (err.message === 'Network Error') {
        errorMessage = 'Cannot connect to server. Check your internet connection.';
      } else if (err.message.includes('already exists')) {
        errorMessage = 'An account with this email already exists.';
      }
      
      setError(errorMessage);
      
      // Log registration failure
      logSecurityEvent('registration_failed', {
        email: formData.email,
        error: err.message,
        status: err.response?.status
      });
      
    } finally {
      setLoading(false);
      setIsSubmitting(false);
    }
  };

  /**
   * Toggles password visibility
   */
  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  /**
   * Toggles confirm password visibility
   */
  const toggleConfirmPasswordVisibility = () => {
    setShowConfirmPassword(!showConfirmPassword);
  };

  /**
   * Handles role change with validation
   */
  const handleRoleChange = (role) => {
    setFormData(prev => ({
      ...prev,
      role,
      // Clear studentId if role changes from student
      ...(prev.role === 'student' && role !== 'student' && { studentId: '' })
    }));
    
    // Clear role-specific validation errors
    if (validationErrors.studentId) {
      setValidationErrors(prev => ({ ...prev, studentId: '' }));
    }
  };

  /**
   * Gets current role object
   */
  const getCurrentRole = () => {
    return roleOptions.find(role => role.value === formData.role) || roleOptions[0];
  };

  // Get password strength info
  const strengthInfo = getPasswordStrengthInfo();

  // Check if form is disabled
  const isFormDisabled = loading || isSubmitting;

  return (
    <>
      <Navbar />
      <div className="auth-container">
        <div className="auth-form">
          {/* Header */}
          <div className="auth-header">
            <div className="auth-logo">
              <svg className="logo-icon" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M12 2L3 7V12C3 16.97 7.03 21 12 21C16.97 21 21 16.97 21 12V7L12 2Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M12 22C17.5228 22 22 17.5228 22 12C22 6.47715 17.5228 2 12 2C6.47715 2 2 6.47715 2 12C2 17.5228 6.47715 22 12 22Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" fill="none"/>
                <path d="M12 15C13.6569 15 15 13.6569 15 12C15 10.3431 13.6569 9 12 9C10.3431 9 9 10.3431 9 12C9 13.6569 10.3431 15 12 15Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
              <h2>Bugema IT Support System</h2>
            </div>
            <p className="auth-subtitle">Create your enterprise account</p>
          </div>

          {/* Progress Bar */}
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{ width: `${formProgress}%` }}
              role="progressbar"
              aria-valuenow={formProgress}
              aria-valuemin="0"
              aria-valuemax="100"
            >
              <span className="progress-text">{formProgress}% Complete</span>
            </div>
          </div>

          {/* Error Message */}
          {error && (
            <div className="error-message" role="alert">
              <div className="error-icon">
                <svg viewBox="0 0 24 24" fill="currentColor">
                  <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"/>
                </svg>
              </div>
              <div className="error-content">
                <p>{error}</p>
              </div>
            </div>
          )}

          {/* Registration Form */}
          <form onSubmit={handleSubmit} className="register-form" noValidate>
            {/* Personal Information Section */}
            <div className="form-section">
              <h3 className="section-title">
                <svg viewBox="0 0 24 24" fill="currentColor" style={{ width: '20px', height: '20px' }}>
                  <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z"/>
                </svg>
                Personal Information
              </h3>
              
              <div className="form-group">
                <label htmlFor="username" className="form-label">
                  Full Name <span className="required">*</span>
                </label>
                <div className={`input-group ${validationErrors.username ? 'error' : ''}`}>
                  <div className="input-icon">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z"/>
                    </svg>
                  </div>
                  <input
                    ref={nameRef}
                    id="username"
                    name="username"
                    type="text"
                    autoComplete="name"
                    value={formData.username}
                    onChange={handleChange}
                    disabled={isFormDisabled}
                    placeholder="Enter your full name"
                    className="form-input"
                    aria-describedby={validationErrors.username ? "username-error" : undefined}
                    aria-required="true"
                  />
                </div>
                {validationErrors.username && (
                  <div id="username-error" className="error-text" role="alert">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"/>
                    </svg>
                    {validationErrors.username}
                  </div>
                )}
              </div>
              
              <div className="form-group">
                <label htmlFor="email" className="form-label">
                  Email Address <span className="required">*</span>
                </label>
                <div className={`input-group ${validationErrors.email ? 'error' : ''}`}>
                  <div className="input-icon">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M20 4H4c-1.1 0-1.99.9-1.99 2L2 18c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2zm0 4l-8 5-8-5V6l8 5 8-5v2z"/>
                    </svg>
                  </div>
                  <input
                    id="email"
                    name="email"
                    type="email"
                    autoComplete="email"
                    value={formData.email}
                    onChange={handleChange}
                    disabled={isFormDisabled}
                    placeholder="your.email@example.com"
                    className="form-input"
                    aria-describedby={validationErrors.email ? "email-error" : undefined}
                    aria-required="true"
                  />
                </div>
                {validationErrors.email && (
                  <div id="email-error" className="error-text" role="alert">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"/>
                    </svg>
                    {validationErrors.email}
                  </div>
                )}
                <div className={`info-note ${isPersonalEmail ? 'warning' : 'info'}`}>
                  <div className="note-content">
                    {isPersonalEmail ? (
                      <p>
                        <strong>Note:</strong> You're using a personal email. For official communication, 
                        we recommend using your university email (<strong>@bugemauniv.ac.ug</strong>) if available.
                      </p>
                    ) : (
                      <p>âœ… Using a verified Bugema University email address</p>
                    )}
                  </div>
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="phone" className="form-label">
                  Phone Number (Optional)
                </label>
                <div className={`input-group ${validationErrors.phone ? 'error' : ''}`}>
                  <div className="input-icon">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M6.62 10.79c1.44 2.83 3.76 5.14 6.59 6.59l2.2-2.2c.27-.27.67-.36 1.02-.24 1.12.37 2.33.57 3.57.57.55 0 1 .45 1 1V20c0 .55-.45 1-1 1-9.39 0-17-7.61-17-17 0-.55.45-1 1-1h3.5c.55 0 1 .45 1 1 0 1.25.2 2.45.57 3.57.11.35.03.74-.25 1.02l-2.2 2.2z"/>
                    </svg>
                  </div>
                  <input
                    id="phone"
                    name="phone"
                    type="tel"
                    autoComplete="tel"
                    value={formData.phone}
                    onChange={handleChange}
                    disabled={isFormDisabled}
                    placeholder="+256 XXX XXX XXX"
                    className="form-input"
                    aria-describedby={validationErrors.phone ? "phone-error" : undefined}
                  />
                </div>
                {validationErrors.phone && (
                  <div id="phone-error" className="error-text" role="alert">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"/>
                    </svg>
                    {validationErrors.phone}
                  </div>
                )}
              </div>
            </div>

            {/* Account Type Section */}
            <div className="form-section">
              <h3 className="section-title">
                <svg viewBox="0 0 24 24" fill="currentColor" style={{ width: '20px', height: '20px' }}>
                  <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
                </svg>
                Account Type
              </h3>
              
              <div className="role-selection">
                {roleOptions.map(option => (
                  <label 
                    key={option.value} 
                    className={`role-option ${formData.role === option.value ? 'selected' : ''}`}
                    onClick={() => handleRoleChange(option.value)}
                  >
                    <input
                      type="radio"
                      name="role"
                      value={option.value}
                      checked={formData.role === option.value}
                      onChange={() => handleRoleChange(option.value)}
                      className="role-radio"
                      disabled={isFormDisabled}
                      aria-label={option.label}
                    />
                    <div className="role-content">
                      <span className="role-icon">{option.icon}</span>
                      <div className="role-info">
                        <span className="role-label">{option.label}</span>
                        <span className="role-description">{option.description}</span>
                        {option.requiresApproval && (
                          <span className="role-notice">
                            <svg viewBox="0 0 24 24" fill="currentColor" style={{ width: '14px', height: '14px', marginRight: '4px' }}>
                              <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
                            </svg>
                            Requires approval
                          </span>
                        )}
                      </div>
                    </div>
                  </label>
                ))}
              </div>
              
              <div className="role-permissions">
                <h4>Permissions for {getCurrentRole().label}:</h4>
                <ul>
                  {getCurrentRole().permissions.map((permission, index) => (
                    <li key={index}>
                      <svg viewBox="0 0 24 24" fill="currentColor" style={{ width: '16px', height: '16px', marginRight: '8px' }}>
                        <path d="M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41z"/>
                      </svg>
                      {permission.replace(/_/g, ' ')}
                    </li>
                  ))}
                </ul>
              </div>
            </div>

            {/* Additional Information Section */}
            <div className="form-section">
              <h3 className="section-title">
                <svg viewBox="0 0 24 24" fill="currentColor" style={{ width: '20px', height: '20px' }}>
                  <path d="M19 3H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm0 16H5V5h14v14zM7 10h2v7H7zm4-3h2v10h-2zm4-3h2v13h-2z"/>
                </svg>
                Additional Information
              </h3>
              
              <div className="form-group">
                <label htmlFor="department" className="form-label">
                  Department <span className="required">*</span>
                </label>
                <div className={`input-group ${validationErrors.department ? 'error' : ''}`}>
                  <div className="input-icon">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M16 6l2.29 2.29-4.88 4.88-4-4L2 16.59 3.41 18l6-6 4 4 6.3-6.29L22 12V6z"/>
                    </svg>
                  </div>
                  <select
                    id="department"
                    name="department"
                    value={formData.department}
                    onChange={handleChange}
                    disabled={isFormDisabled}
                    className="form-select"
                    aria-describedby={validationErrors.department ? "department-error" : undefined}
                    aria-required="true"
                  >
                    <option value="">Select your department</option>
                    {suggestedDepartments.map(dept => (
                      <option key={dept.value} value={dept.value}>
                        {dept.label}
                      </option>
                    ))}
                  </select>
                </div>
                {validationErrors.department && (
                  <div id="department-error" className="error-text" role="alert">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"/>
                    </svg>
                    {validationErrors.department}
                  </div>
                )}
              </div>

              {/* Student-specific field */}
              {formData.role === 'student' && (
                <div className="form-group">
                  <label htmlFor="studentId" className="form-label">
                    Student ID <span className="required">*</span>
                  </label>
                  <div className={`input-group ${validationErrors.studentId ? 'error' : ''}`}>
                    <div className="input-icon">
                      <svg viewBox="0 0 24 24" fill="currentColor">
                        <path d="M20 4H4c-1.1 0-1.99.9-1.99 2L2 18c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2zm-5 14H4v-4h11v4zm0-5H4V9h11v4zm5 5h-4V9h4v9z"/>
                      </svg>
                    </div>
                    <input
                      id="studentId"
                      name="studentId"
                      type="text"
                      value={formData.studentId}
                      onChange={handleChange}
                      disabled={isFormDisabled}
                      placeholder="Format: 22/BIT/BU/R/0010"
                      className="form-input"
                      aria-describedby={validationErrors.studentId ? "studentId-error" : undefined}
                      aria-required={formData.role === 'student'}
                    />
                  </div>
                  {validationErrors.studentId && (
                    <div id="studentId-error" className="error-text" role="alert">
                      <svg viewBox="0 0 24 24" fill="currentColor">
                        <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"/>
                      </svg>
                      {validationErrors.studentId}
                    </div>
                  )}
                  <div className="info-note info">
                    <div className="note-content">
                      <p><strong>Format:</strong> Year/Department/Campus/Level/Number (e.g., 22/BIT/BU/R/0010)</p>
                      <p><strong>Campus codes:</strong> BU (Main Campus), MA (Kampala), KA (Kasese), AR (Arua)</p>
                    </div>
                  </div>
                </div>
              )}

              {/* Technician/Admin note */}
              {(formData.role === 'technician' || formData.role === 'admin') && (
                <div className="info-note warning">
                  <div className="note-icon">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"/>
                    </svg>
                  </div>
                  <div className="note-content">
                    <strong>Verification Required</strong>
                    <p>Technician and administrator accounts require manual verification by system administrators. You will receive email notification once your account is approved.</p>
                  </div>
                </div>
              )}
            </div>

            {/* Security Section */}
            <div className="form-section">
              <h3 className="section-title">
                <svg viewBox="0 0 24 24" fill="currentColor" style={{ width: '20px', height: '20px' }}>
                  <path d="M18 8h-1V6c0-2.76-2.24-5-5-5S7 3.24 7 6v2H6c-1.1 0-2 .9-2 2v10c0 1.1.9 2 2 2h12c1.1 0 2-.9 2-2V10c0-1.1-.9-2-2-2zm-6 9c-1.1 0-2-.9-2-2s.9-2 2-2 2 .9 2 2-.9 2-2 2zm3.1-9H8.9V6c0-1.71 1.39-3.1 3.1-3.1 1.71 0 3.1 1.39 3.1 3.1v2z"/>
                </svg>
                Security
              </h3>
              
              <div className="form-group">
                <label htmlFor="password" className="form-label">
                  Password <span className="required">*</span>
                </label>
                <div className={`input-group ${validationErrors.password ? 'error' : ''}`}>
                  <div className="input-icon">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M18 8h-1V6c0-2.76-2.24-5-5-5S7 3.24 7 6v2H6c-1.1 0-2 .9-2 2v10c0 1.1.9 2 2 2h12c1.1 0 2-.9 2-2V10c0-1.1-.9-2-2-2zm-6 9c-1.1 0-2-.9-2-2s.9-2 2-2 2 .9 2 2-.9 2-2 2zm3.1-9H8.9V6c0-1.71 1.39-3.1 3.1-3.1 1.71 0 3.1 1.39 3.1 3.1v2z"/>
                    </svg>
                  </div>
                  <input
                    id="password"
                    name="password"
                    type={showPassword ? "text" : "password"}
                    autoComplete="new-password"
                    value={formData.password}
                    onChange={handleChange}
                    disabled={isFormDisabled}
                    placeholder="Create a strong password"
                    className="form-input"
                    aria-describedby={validationErrors.password ? "password-error" : undefined}
                    aria-required="true"
                  />
                  <button
                    type="button"
                    onClick={togglePasswordVisibility}
                    className="password-toggle"
                    disabled={isFormDisabled}
                    aria-label={showPassword ? "Hide password" : "Show password"}
                  >
                    {showPassword ? (
                      <svg viewBox="0 0 24 24" fill="currentColor">
                        <path d="M12 7c2.76 0 5 2.24 5 5 0 .65-.13 1.26-.36 1.83l2.92 2.92c1.51-1.26 2.7-2.89 3.43-4.75-1.73-4.39-6-7.5-11-7.5-1.4 0-2.74.25-3.98.7l2.16 2.16C10.74 7.13 11.35 7 12 7zM2 4.27l2.28 2.28.46.46C3.08 8.3 1.78 10.02 1 12c1.73 4.39 6 7.5 11 7.5 1.55 0 3.03-.3 4.38-.84l.42.42L19.73 22 21 20.73 3.27 3 2 4.27zM7.53 9.8l1.55 1.55c-.05.21-.08.43-.08.65 0 1.66 1.34 3 3 3 .22 0 .44-.03.65-.08l1.55 1.55c-.67.33-1.41.53-2.2.53-2.76 0-5-2.24-5-5 0-.79.2-1.53.53-2.2zm4.31-.78l3.15 3.15.02-.16c0-1.66-1.34-3-3-3l-.17.01z"/>
                      </svg>
                    ) : (
                      <svg viewBox="0 0 24 24" fill="currentColor">
                        <path d="M12 4.5C7 4.5 2.73 7.61 1 12c1.73 4.39 6 7.5 11 7.5s9.27-3.11 11-7.5c-1.73-4.39-6-7.5-11-7.5zM12 17c-2.76 0-5-2.24-5-5s2.24-5 5-5 5 2.24 5 5-2.24 5-5 5zm0-8c-1.66 0-3 1.34-3 3s1.34 3 3 3 3-1.34 3-3-1.34-3-3-3z"/>
                      </svg>
                    )}
                  </button>
                </div>
                
                {/* Password Strength Meter */}
                {formData.password && (
                  <div className="password-strength">
                    <div className="strength-meter">
                      <div 
                        className={`strength-bar ${strengthInfo.className}`}
                        style={{ width: `${passwordStrength}%` }}
                      ></div>
                    </div>
                    <div className="strength-info">
                      <span className="strength-label">Strength: {strengthInfo.label}</span>
                      <span className="strength-score">{passwordStrength}%</span>
                    </div>
                  </div>
                )}
                
                {validationErrors.password && (
                  <div id="password-error" className="error-text" role="alert">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"/>
                    </svg>
                    {validationErrors.password}
                  </div>
                )}
                
                <div className="password-requirements">
                  <p><strong>Password requirements:</strong></p>
                  <ul>
                    <li className={formData.password.length >= 8 ? 'valid' : 'invalid'}>
                      At least 8 characters
                    </li>
                    <li className={/[A-Z]/.test(formData.password) ? 'valid' : 'invalid'}>
                      One uppercase letter
                    </li>
                    <li className={/[a-z]/.test(formData.password) ? 'valid' : 'invalid'}>
                      One lowercase letter
                    </li>
                    <li className={/[0-9]/.test(formData.password) ? 'valid' : 'invalid'}>
                      One number
                    </li>
                    <li className={/[^A-Za-z0-9]/.test(formData.password) ? 'valid' : 'invalid'}>
                      One special character
                    </li>
                  </ul>
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="confirmPassword" className="form-label">
                  Confirm Password <span className="required">*</span>
                </label>
                <div className={`input-group ${validationErrors.confirmPassword ? 'error' : ''}`}>
                  <div className="input-icon">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M18 8h-1V6c0-2.76-2.24-5-5-5S7 3.24 7 6v2H6c-1.1 0-2 .9-2 2v10c0 1.1.9 2 2 2h12c1.1 0 2-.9 2-2V10c0-1.1-.9-2-2-2zm-6 9c-1.1 0-2-.9-2-2s.9-2 2-2 2 .9 2 2-.9 2-2 2zm3.1-9H8.9V6c0-1.71 1.39-3.1 3.1-3.1 1.71 0 3.1 1.39 3.1 3.1v2z"/>
                    </svg>
                  </div>
                  <input
                    id="confirmPassword"
                    name="confirmPassword"
                    type={showConfirmPassword ? "text" : "password"}
                    autoComplete="new-password"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                    disabled={isFormDisabled}
                    placeholder="Re-enter your password"
                    className="form-input"
                    aria-describedby={validationErrors.confirmPassword ? "confirmPassword-error" : undefined}
                    aria-required="true"
                  />
                  <button
                    type="button"
                    onClick={toggleConfirmPasswordVisibility}
                    className="password-toggle"
                    disabled={isFormDisabled}
                    aria-label={showConfirmPassword ? "Hide password" : "Show password"}
                  >
                    {showConfirmPassword ? (
                      <svg viewBox="0 0 24 24" fill="currentColor">
                        <path d="M12 7c2.76 0 5 2.24 5 5 0 .65-.13 1.26-.36 1.83l2.92 2.92c1.51-1.26 2.7-2.89 3.43-4.75-1.73-4.39-6-7.5-11-7.5-1.4 0-2.74.25-3.98.7l2.16 2.16C10.74 7.13 11.35 7 12 7zM2 4.27l2.28 2.28.46.46C3.08 8.3 1.78 10.02 1 12c1.73 4.39 6 7.5 11 7.5 1.55 0 3.03-.3 4.38-.84l.42.42L19.73 22 21 20.73 3.27 3 2 4.27zM7.53 9.8l1.55 1.55c-.05.21-.08.43-.08.65 0 1.66 1.34 3 3 3 .22 0 .44-.03.65-.08l1.55 1.55c-.67.33-1.41.53-2.2.53-2.76 0-5-2.24-5-5 0-.79.2-1.53.53-2.2zm4.31-.78l3.15 3.15.02-.16c0-1.66-1.34-3-3-3l-.17.01z"/>
                      </svg>
                    ) : (
                      <svg viewBox="0 0 24 24" fill="currentColor">
                        <path d="M12 4.5C7 4.5 2.73 7.61 1 12c1.73 4.39 6 7.5 11 7.5s9.27-3.11 11-7.5c-1.73-4.39-6-7.5-11-7.5zM12 17c-2.76 0-5-2.24-5-5s2.24-5 5-5 5 2.24 5 5-2.24 5-5 5zm0-8c-1.66 0-3 1.34-3 3s1.34 3 3 3 3-1.34 3-3-1.34-3-3-3z"/>
                      </svg>
                    )}
                  </button>
                </div>
                {validationErrors.confirmPassword && (
                  <div id="confirmPassword-error" className="error-text" role="alert">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"/>
                    </svg>
                    {validationErrors.confirmPassword}
                  </div>
                )}
              </div>
            </div>

            {/* Terms and Conditions */}
            <div className="form-section">
              <h3 className="section-title">
                <svg viewBox="0 0 24 24" fill="currentColor" style={{ width: '20px', height: '20px' }}>
                  <path d="M19 3H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm0 16H5V5h14v14zM17.99 9l-1.41-1.42-6.59 6.59-2.58-2.57-1.42 1.41 4 3.99z"/>
                </svg>
                Terms and Conditions
              </h3>
              
              <div className="form-group checkbox-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    name="agreeToTerms"
                    checked={formData.agreeToTerms}
                    onChange={handleChange}
                    disabled={isFormDisabled}
                    className="checkbox-input"
                    aria-describedby={validationErrors.agreeToTerms ? "terms-error" : undefined}
                    aria-required="true"
                  />
                  <span className="checkbox-custom"></span>
                  <span className="checkbox-text">
                    I agree to the{' '}
                    <Link to="/terms" target="_blank" className="link">
                      Terms of Service
                    </Link>
                    {' '}and{' '}
                    <Link to="/privacy" target="_blank" className="link">
                      Privacy Policy
                    </Link>
                    <span className="required">*</span>
                  </span>
                </label>
                {validationErrors.agreeToTerms && (
                  <div id="terms-error" className="error-text" role="alert">
                    <svg viewBox="0 0 24 24" fill="currentColor">
                      <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"/>
                    </svg>
                    {validationErrors.agreeToTerms}
                  </div>
                )}
              </div>
              
              <div className="form-group checkbox-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    name="receiveNotifications"
                    checked={formData.receiveNotifications}
                    onChange={handleChange}
                    disabled={isFormDisabled}
                    className="checkbox-input"
                  />
                  <span className="checkbox-custom"></span>
                  <span className="checkbox-text">
                    I want to receive notifications about system updates and important announcements
                  </span>
                </label>
              </div>
              
              <div className="info-note">
                <div className="note-content">
                  <p><strong>Important:</strong> By creating an account, you acknowledge that:</p>
                  <ul>
                    <li>You are responsible for maintaining the confidentiality of your account</li>
                    <li>You must provide accurate information during registration</li>
                    <li>The IT Support System may be monitored for security and compliance purposes</li>
                    <li>Misuse of the system may result in account suspension or disciplinary action</li>
                  </ul>
                </div>
              </div>
            </div>
            
            {/* Submit Button */}
            <div className="form-group">
              <button
                type="submit"
                disabled={isFormDisabled || formProgress < 100}
                className={`auth-btn ${loading ? 'loading' : ''}`}
                aria-busy={loading}
              >
                {loading ? (
                  <>
                    <span className="spinner"></span>
                    Creating Account...
                  </>
                ) : (
                  'Create Account'
                )}
              </button>
            </div>
          </form>
          
          {/* Footer */}
          <div className="auth-footer">
            <p className="auth-link">
              Already have an account?{' '}
              <Link 
                to="/login" 
                className="link"
                aria-disabled={isFormDisabled}
              >
                Login here
              </Link>
            </p>
            <p className="auth-version">
              IT Support System v{process.env.REACT_APP_VERSION || '1.0.0'} â€¢ Bugema University
            </p>
          </div>

          {/* Security Footer */}
          <div className="security-footer">
            <p className="security-notice">
              For registration assistance, contact IT Support:
            </p>
            <p className="security-contact">
              ðŸ“§ <a href="mailto:support@bugemauniv.ac.ug">support@bugemauniv.ac.ug</a> â€¢ 
              ðŸ“ž <a href="tel:+256784845785">+256 784-845-785</a>
            </p>
          </div>
        </div>
      </div>
    </>
  );
};

export default Register;
