// src/App.js - PRODUCTION READY COMPLETE VERSION
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Context
import { AuthProvider } from './context/AuthContext';
import { ThemeProvider } from './context/ThemeContext';

// Components
import ErrorBoundary from './components/ErrorBoundary';
import ProtectedRoute from './components/ProtectedRoute';
import SystemStatusBar from './components/SystemStatusBar';
import ConnectionStatus from './components/ConnectionStatus';
import LoadingScreen from './components/LoadingScreen';
import MaintenanceBanner from './components/MaintenanceBanner';

// Services
import { authService } from './services/authService';

// Pages
import Login from './pages/Login';
import Register from './pages/Register';
import EmailVerification from './pages/EmailVerification';
import ForgotPassword from './pages/ForgotPassword';
import ResetPassword from './pages/ResetPassword';
import StaffDashboard from './pages/StaffDashboard';
import AdminDashboard from './pages/AdminDashboard';
import TechnicianDashboard from './pages/TechnicianDashboard';
import StudentDashboard from './pages/StudentDashboard';
import RoleBasedDashboard from './pages/RoleBasedDashboard';
import Tickets from './pages/Tickets';
import TicketDetail from './pages/TicketDetail';
import CreateTicket from './pages/CreateTicket';
import Reports from './pages/Reports';
import Profile from './pages/Profile';
import Settings from './pages/Settings';
import NotFound from './pages/NotFound';

// Styles
import './styles/App.css';

// Create query client with production settings
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: (failureCount, error) => {
        // Don't retry on 401/403 errors
        if (error?.status === 401 || error?.status === 403) {
          return false;
        }
        // Retry once for network errors
        return failureCount < 1;
      },
      refetchOnWindowFocus: false,
      staleTime: 5 * 60 * 1000, // 5 minutes
      cacheTime: 10 * 60 * 1000, // 10 minutes
    },
    mutations: {
      retry: 0,
    },
  },
});

function App() {
  const [isInitializing, setIsInitializing] = useState(true);
  const [backendStatus, setBackendStatus] = useState('checking');
  const [maintenanceMode, setMaintenanceMode] = useState(false);

  useEffect(() => {
    const initializeApp = async () => {
      try {
        console.log('ðŸš€ Initializing Bugema University IT Support System...');
        console.log('ðŸ“± Version:', process.env.REACT_APP_VERSION || '2.0.0');
        console.log('ðŸŒ Environment:', process.env.NODE_ENV || 'development');
        console.log('ðŸ”— API URL:', process.env.REACT_APP_API_URL || 'http://localhost:5002');
        
        // Initialize auth service
        authService.initializeAuth();
        
        // Check backend health (non-blocking)
        checkBackendHealth();
        
        // Check for maintenance mode
        checkMaintenanceMode();
        
        console.log('âœ… App initialized successfully');
      } catch (error) {
        console.error('âŒ App initialization failed:', error);
      } finally {
        // Minimal delay for better UX
        setTimeout(() => {
          setIsInitializing(false);
        }, 300);
      }
    };

    const checkBackendHealth = async () => {
      try {
        const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5002';
        const response = await fetch(`${apiUrl}/api/health`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
          // Short timeout for health check
          signal: AbortSignal.timeout(5000)
        });
        
        if (response.ok) {
          const data = await response.json();
          console.log('âœ… Backend health check:', data);
          
          if (data.database === 'connected') {
            setBackendStatus('online');
          } else {
            setBackendStatus('degraded');
            console.warn('âš ï¸ Backend online but database disconnected');
          }
        } else {
          setBackendStatus('offline');
          console.warn('âš ï¸ Backend health check failed:', response.status);
        }
      } catch (error) {
        console.log('âš ï¸ Backend not reachable:', error.message);
        setBackendStatus('offline');
      }
    };

    const checkMaintenanceMode = () => {
      // Check if maintenance mode is forced via URL
      const urlParams = new URLSearchParams(window.location.search);
      const forceMaintenance = urlParams.get('maintenance') === 'true';
      
      // Check localStorage for saved maintenance state
      const savedMaintenance = localStorage.getItem('maintenance_mode');
      
      if (forceMaintenance || savedMaintenance === 'true') {
        setMaintenanceMode(true);
        localStorage.setItem('maintenance_mode', 'true');
        
        if (forceMaintenance) {
          console.log('ðŸ”§ Maintenance mode activated via URL parameter');
        }
      }
    };

    initializeApp();

    // Setup periodic health check (every 2 minutes)
    const healthCheckInterval = setInterval(checkBackendHealth, 2 * 60 * 1000);

    return () => {
      clearInterval(healthCheckInterval);
    };
  }, []);

  const handleResumeAccess = () => {
    setMaintenanceMode(false);
    localStorage.removeItem('maintenance_mode');
    
    // Remove maintenance parameter from URL
    const url = new URL(window.location.href);
    url.searchParams.delete('maintenance');
    window.history.replaceState({}, '', url);
  };

  if (isInitializing) {
    return (
      <LoadingScreen 
        message="Initializing Bugema IT Support System..."
        subtitle="Loading secure environment..."
        showProgress
      />
    );
  }

  if (maintenanceMode && !window.location.pathname.includes('/login')) {
    return (
      <div className="app-container">
        <MaintenanceBanner onResume={handleResumeAccess} />
      </div>
    );
  }

  return (
    <ErrorBoundary>
      <QueryClientProvider client={queryClient}>
        <ThemeProvider>
          <AuthProvider>
            <Router>
              <div className="app-container">
                <SystemStatusBar backendStatus={backendStatus} />
                <ConnectionStatus status={backendStatus} />
                
                <Routes>
                  {/* Public Routes */}
                  <Route path="/" element={<Navigate to="/login" replace />} />
                  <Route path="/login" element={<Login />} />
                  <Route path="/register" element={<Register />} />
                  <Route path="/verify-email/:token" element={<EmailVerification />} />
                  <Route path="/forgot-password" element={<ForgotPassword />} />
                  <Route path="/reset-password/:token" element={<ResetPassword />} />

                  {/* Protected Routes */}
                  <Route element={<ProtectedRoute />}>
                    <Route path="/dashboard" element={<RoleBasedDashboard />} />
                    <Route path="/admin/*" element={<AdminDashboard />} />
                    <Route path="/technician/*" element={<TechnicianDashboard />} />
                    <Route path="/staff/*" element={<StaffDashboard />} />
                    <Route path="/student/*" element={<StudentDashboard />} />
                    
                    {/* Ticket Management */}
                    <Route path="/tickets/create" element={<CreateTicket />} />
                    <Route path="/tickets/:id" element={<TicketDetail />} />
                    <Route path="/tickets" element={<Tickets />} />
                    
                    {/* Reports & Analytics */}
                    <Route path="/reports" element={<Reports />} />
                    
                    {/* User Management */}
                    <Route path="/profile" element={<Profile />} />
                    <Route path="/settings" element={<Settings />} />
                    
                    {/* Fallback route */}
                    <Route path="*" element={<NotFound />} />
                  </Route>
                  
                  {/* Catch-all redirect */}
                  <Route path="*" element={<Navigate to="/dashboard" replace />} />
                </Routes>
                
                {/* Toast Notifications */}
                <ToastContainer
                  position="top-right"
                  autoClose={5000}
                  hideProgressBar={false}
                  newestOnTop
                  closeOnClick
                  rtl={false}
                  pauseOnFocusLoss
                  draggable
                  pauseOnHover
                  theme="light"
                  style={{ zIndex: 9999 }}
                />
              </div>
            </Router>
          </AuthProvider>
        </ThemeProvider>
      </QueryClientProvider>
    </ErrorBoundary>
  );
}

export default App;
