import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import ticketService from '../services/ticketService';
import { useAuth } from '../context/AuthContext';
import '../styles/CreateTicket.css';

const CreateTicket = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    issueType: 'software',
    urgency: 'medium',
    location: '',
    deviceInfo: '',
    operatingSystem: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [charCount, setCharCount] = useState({ title: 0, description: 0 });
  const [showConfirmation, setShowConfirmation] = useState(false);

  const navigate = useNavigate();
  const { user } = useAuth();

  // Character counting
  useEffect(() => {
    setCharCount({
      title: formData.title.length,
      description: formData.description.length
    });
  }, [formData.title, formData.description]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    // Enhanced client-side validation
    if (formData.title.length < 5) {
      setError('Title must be at least 5 characters long');
      setLoading(false);
      return;
    }

    if (formData.description.length < 10) {
      setError('Description must be at least 10 characters long');
      setLoading(false);
      return;
    }

    try {
      await ticketService.createTicket(formData);
      setShowConfirmation(true);
      
      // Redirect after 2 seconds
      setTimeout(() => {
        navigate('/tickets');
      }, 2000);
    } catch (err) {
      setError(err.response?.data?.message || 
               err.response?.data?.errors?.join(', ') || 
               'Failed to create ticket. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const issueTypes = [
    { value: 'hardware', label: 'Hardware', description: 'Computer, printer, network device issues' },
    { value: 'software', label: 'Software', description: 'Application, operating system, software installation' },
    { value: 'network', label: 'Network', description: 'Internet, WiFi, connectivity problems' },
    { value: 'account', label: 'Account', description: 'Login, password, access rights issues' },
    { value: 'email', label: 'Email', description: 'University email, calendar, or communication issues' },
    { value: 'printing', label: 'Printing', description: 'Printer setup, printing problems' },
    { value: 'other', label: 'Other', description: 'Any other technical issues' }
  ];

  const urgencyLevels = [
    { value: 'low', label: 'Low', description: 'Minor issue, no immediate impact' },
    { value: 'medium', label: 'Medium', description: 'Moderate impact on work' },
    { value: 'high', label: 'High', description: 'Significant impact, work blocked' },
    { value: 'critical', label: 'Critical', description: 'System down, urgent attention required' }
  ];

  const operatingSystems = [
    'Windows 10',
    'Windows 11', 
    'macOS',
    'Linux',
    'Chrome OS',
    'Other'
  ];

  if (showConfirmation) {
    return (
      <>
        <Navbar />
        <div className="create-ticket">
          <div className="confirmation-message">
            <div className="confirmation-icon">✅</div>
            <h2>Ticket Created Successfully!</h2>
            <p>Your support ticket has been submitted and will be addressed shortly.</p>
            <p>You will receive a confirmation email with your ticket details.</p>
            <button 
              onClick={() => navigate('/tickets')}
              className="btn-primary"
            >
              View My Tickets
            </button>
          </div>
        </div>
      </>
    );
  }

  return (
    <>
      <Navbar />
      <div className="create-ticket">
        <div className="create-ticket-header">
          <h1>Create New Support Ticket</h1>
          <p className="subtitle">Fill out the form below to request technical support</p>
        </div>

        <div className="create-ticket-form">
          {error && (
            <div className="error-message">
              <span className="error-icon">⚠️</span>
              {error}
            </div>
          )}
          
          <form onSubmit={handleSubmit}>
            {/* Basic Information Section */}
            <div className="form-section">
              <h3 className="section-title">Basic Information</h3>
              
              <div className="form-group">
                <label>Title *</label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  required
                  minLength="5"
                  maxLength="100"
                  placeholder="Brief, descriptive title of the issue"
                  className={charCount.title > 0 && charCount.title < 5 ? 'error' : ''}
                />
                <div className="char-count">
                  {charCount.title}/100 characters • Minimum 5 characters required
                </div>
              </div>

              <div className="form-group">
                <label>Description *</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  required
                  minLength="10"
                  maxLength="1000"
                  rows="6"
                  placeholder="Please provide detailed information about the issue:
• What exactly is happening?
• When did it start?
• Steps to reproduce the issue
• Any error messages received"
                  className={charCount.description > 0 && charCount.description < 10 ? 'error' : ''}
                />
                <div className="char-count">
                  {charCount.description}/1000 characters • Minimum 10 characters required
                </div>
              </div>
            </div>

            {/* Technical Details Section */}
            <div className="form-section">
              <h3 className="section-title">Technical Details</h3>
              
              <div className="form-row">
                <div className="form-group">
                  <label>Issue Type *</label>
                  <select
                    name="issueType"
                    value={formData.issueType}
                    onChange={handleChange}
                    required
                  >
                    {issueTypes.map(type => (
                      <option key={type.value} value={type.value}>
                        {type.label}
                      </option>
                    ))}
                  </select>
                  <div className="field-description">
                    {issueTypes.find(t => t.value === formData.issueType)?.description}
                  </div>
                </div>

                <div className="form-group">
                  <label>Urgency Level *</label>
                  <select
                    name="urgency"
                    value={formData.urgency}
                    onChange={handleChange}
                    required
                  >
                    {urgencyLevels.map(level => (
                      <option key={level.value} value={level.value}>
                        {level.label}
                      </option>
                    ))}
                  </select>
                  <div className="field-description">
                    {urgencyLevels.find(u => u.value === formData.urgency)?.description}
                  </div>
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Operating System</label>
                  <select
                    name="operatingSystem"
                    value={formData.operatingSystem}
                    onChange={handleChange}
                  >
                    <option value="">Select OS (Optional)</option>
                    {operatingSystems.map(os => (
                      <option key={os} value={os}>{os}</option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label>Device Information</label>
                  <input
                    type="text"
                    name="deviceInfo"
                    value={formData.deviceInfo}
                    onChange={handleChange}
                    placeholder="e.g., Dell Latitude 5420, MacBook Pro 2023 (Optional)"
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Location</label>
                <input
                  type="text"
                  name="location"
                  value={formData.location}
                  onChange={handleChange}
                  placeholder="Where is the issue occurring? (e.g., Room 101, Science Building Lab 2)"
                />
              </div>
            </div>

            {/* User Information Section */}
            <div className="form-section">
              <h3 className="section-title">Your Information</h3>
              <div className="user-info-display">
                <div className="info-item">
                  <span className="info-label">Name:</span>
                  <span className="info-value">{user?.username}</span>
                </div>
                <div className="info-item">
                  <span className="info-label">Email:</span>
                  <span className="info-value">{user?.email}</span>
                </div>
                <div className="info-item">
                  <span className="info-label">Department:</span>
                  <span className="info-value">{user?.department}</span>
                </div>
                <div className="info-item">
                  <span className="info-label">Role:</span>
                  <span className="info-value">{user?.role}</span>
                </div>
              </div>
            </div>

            {/* Form Actions */}
            <div className="form-actions">
              <button
                type="button"
                onClick={() => navigate('/tickets')}
                className="btn-secondary"
                disabled={loading}
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={loading || formData.title.length < 5 || formData.description.length < 10}
                className={`btn-primary ${loading ? 'loading' : ''}`}
              >
                {loading ? (
                  <>
                    <span className="spinner"></span>
                    Creating Ticket...
                  </>
                ) : (
                  'Create Support Ticket'
                )}
              </button>
            </div>

            <div className="form-footer">
              <p className="disclaimer">
                * Required fields. By submitting this ticket, you agree that IT staff may 
                access your system if necessary to resolve the issue.
              </p>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default CreateTicket;
