import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import Navbar from '../components/Navbar';
import ticketService from '../services/ticketService';
import userService from '../services/userService';
import '../styles/TicketDetail.css';

const TicketDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  
  const [ticket, setTicket] = useState(null);
  const [comments, setComments] = useState([]);
  const [activities, setActivities] = useState([]);
  const [technicians, setTechnicians] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [isInternal, setIsInternal] = useState(false);
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);
  const [updateData, setUpdateData] = useState({
    status: '',
    assignedTo: ''
  });

  useEffect(() => {
    fetchTicketData();
    if (user.role === 'technician') {
      fetchTechnicians();
    }
  }, [id]);

  const fetchTicketData = async () => {
    try {
      const [ticketData, commentsData, activitiesData] = await Promise.all([
        ticketService.getTicket(id),
        ticketService.getComments(id),
        ticketService.getActivities(id)
      ]);

      setTicket(ticketData);
      setComments(commentsData);
      setActivities(activitiesData);
      setUpdateData({
        status: ticketData.status,
        assignedTo: ticketData.assignedTo?._id || ''
      });
    } catch (error) {
      console.error('Error fetching ticket data:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchTechnicians = async () => {
    try {
      const response = await userService.getTechnicians();
      setTechnicians(response);
    } catch (error) {
      console.error('Error fetching technicians:', error);
    }
  };

  const handleStatusUpdate = async () => {
    if (!updateData.status) return;
    
    setUpdating(true);
    try {
      await ticketService.updateTicket(id, { status: updateData.status });
      await fetchTicketData();
    } catch (error) {
      console.error('Error updating ticket:', error);
    } finally {
      setUpdating(false);
    }
  };

  const handleAssignment = async () => {
    setUpdating(true);
    try {
      await ticketService.updateTicket(id, { 
        assignedTo: updateData.assignedTo || null 
      });
      await fetchTicketData();
    } catch (error) {
      console.error('Error assigning ticket:', error);
    } finally {
      setUpdating(false);
    }
  };

  const handleAddComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;

    try {
      await ticketService.addComment(id, {
        content: newComment,
        isInternal: user.role === 'technician' ? isInternal : false
      });
      
      setNewComment('');
      setIsInternal(false);
      await fetchTicketData(); // Refresh comments and activities
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  if (loading) {
    return (
      <>
        <Navbar />
        <div className="loading">Loading ticket details...</div>
      </>
    );
  }

  if (!ticket) {
    return (
      <>
        <Navbar />
        <div className="error-container">
          <h2>Ticket not found</h2>
          <button onClick={() => navigate('/tickets')} className="btn-primary">
            Back to Tickets
          </button>
        </div>
      </>
    );
  }

  return (
    <>
      <Navbar />
      <div className="ticket-detail">
        {/* Header */}
        <div className="ticket-detail-header">
          <div className="ticket-title-section">
            <h1>{ticket.title}</h1>
            <div className="ticket-meta">
              <span className={`status-badge status-${ticket.status}`}>
                {ticket.status}
              </span>
              <span className={`urgency-badge urgency-${ticket.urgency}`}>
                {ticket.urgency}
              </span>
              <span className="ticket-id">ID: {ticket.ticketId}</span>
            </div>
          </div>
          <button onClick={() => navigate('/tickets')} className="btn-secondary">
            Back to Tickets
          </button>
        </div>

        <div className="ticket-detail-content">
          {/* Left Column - Ticket Info and Comments */}
          <div className="ticket-left-column">
            {/* Ticket Description */}
            <div className="ticket-section">
              <h3>Description</h3>
              <div className="ticket-description">
                <p>{ticket.description}</p>
              </div>
              <div className="ticket-info-grid">
                <div className="info-item">
                  <strong>Issue Type:</strong>
                  <span>{ticket.issueType}</span>
                </div>
                <div className="info-item">
                  <strong>Created By:</strong>
                  <span>{ticket.createdBy?.username}</span>
                </div>
                <div className="info-item">
                  <strong>Created Date:</strong>
                  <span>{formatDate(ticket.createdAt)}</span>
                </div>
                <div className="info-item">
                  <strong>Last Updated:</strong>
                  <span>{formatDate(ticket.updatedAt)}</span>
                </div>
              </div>
            </div>

            {/* Comments Section */}
            <div className="ticket-section">
              <h3>Comments</h3>
              <div className="comments-list">
                {comments.length === 0 ? (
                  <p className="no-comments">No comments yet.</p>
                ) : (
                  comments.map(comment => (
                    <div key={comment._id} className={`comment ${comment.isInternal ? 'internal' : ''}`}>
                      <div className="comment-header">
                        <strong>{comment.userId?.username}</strong>
                        {comment.isInternal && <span className="internal-badge">Internal</span>}
                        <span className="comment-date">{formatDate(comment.createdAt)}</span>
                      </div>
                      <div className="comment-content">
                        {comment.content}
                      </div>
                    </div>
                  ))
                )}
              </div>

              {/* Add Comment Form */}
              <form onSubmit={handleAddComment} className="add-comment-form">
                <div className="form-group">
                  <textarea
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Add a comment..."
                    rows="4"
                    required
                  />
                </div>
                {user.role === 'technician' && (
                  <div className="form-check">
                    <input
                      type="checkbox"
                      id="internal"
                      checked={isInternal}
                      onChange={(e) => setIsInternal(e.target.checked)}
                    />
                    <label htmlFor="internal">Internal comment (visible to technicians only)</label>
                  </div>
                )}
                <button type="submit" className="btn-primary">
                  Add Comment
                </button>
              </form>
            </div>
          </div>

          {/* Right Column - Admin Controls and Activity */}
          <div className="ticket-right-column">
            {/* Admin Controls */}
            {user.role === 'technician' && (
              <div className="ticket-section admin-controls">
                <h3>Ticket Management</h3>
                
                {/* Status Update */}
                <div className="control-group">
                  <label>Update Status</label>
                  <select
                    value={updateData.status}
                    onChange={(e) => setUpdateData(prev => ({ ...prev, status: e.target.value }))}
                  >
                    <option value="open">Open</option>
                    <option value="in-progress">In Progress</option>
                    <option value="resolved">Resolved</option>
                    <option value="closed">Closed</option>
                  </select>
                  <button 
                    onClick={handleStatusUpdate}
                    disabled={updating || updateData.status === ticket.status}
                    className="btn-primary"
                  >
                    {updating ? 'Updating...' : 'Update Status'}
                  </button>
                </div>

                {/* Assignment */}
                <div className="control-group">
                  <label>Assign to Technician</label>
                  <select
                    value={updateData.assignedTo}
                    onChange={(e) => setUpdateData(prev => ({ ...prev, assignedTo: e.target.value }))}
                  >
                    <option value="">Unassigned</option>
                    {technicians.map(tech => (
                      <option key={tech._id} value={tech._id}>
                        {tech.username}
                      </option>
                    ))}
                  </select>
                  <button 
                    onClick={handleAssignment}
                    disabled={updating}
                    className="btn-primary"
                  >
                    {updating ? 'Assigning...' : 'Assign'}
                  </button>
                </div>

                {/* Current Assignment */}
                {ticket.assignedTo && (
                  <div className="current-assignment">
                    <strong>Currently assigned to:</strong>
                    <span>{ticket.assignedTo.username}</span>
                  </div>
                )}
              </div>
            )}

            {/* Activity Log */}
            <div className="ticket-section activity-log">
              <h3>Activity Log</h3>
              <div className="activities-list">
                {activities.length === 0 ? (
                  <p className="no-activities">No activity recorded.</p>
                ) : (
                  activities.map(activity => (
                    <div key={activity._id} className="activity-item">
                      <div className="activity-header">
                        <strong>{activity.userId?.username}</strong>
                        <span className="activity-date">{formatDate(activity.createdAt)}</span>
                      </div>
                      <div className="activity-description">
                        {activity.description}
                        {activity.oldValue && activity.newValue && (
                          <span className="activity-changes">
                            ({activity.oldValue} â†’ {activity.newValue})
                          </span>
                        )}
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default TicketDetail;
