import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import TicketCard from '../components/TicketCard';
import LoadingSpinner from '../components/LoadingSpinner';
import ErrorBoundary from '../components/ErrorBoundary';
import ticketService from '../services/ticketService';
import { debounce } from '../utils/performance';
import { validateFilters } from '../utils/validation';
import '../styles/Tickets.css';

// Constants for configuration
const TICKET_CONFIG = {
  PAGE_SIZE: 20,
  DEBOUNCE_DELAY: 500,
  MAX_SELECTION: 100,
  EXPORT_FORMAT: 'csv'
};

const STATUS_OPTIONS = [
  { value: '', label: 'All Status' },
  { value: 'open', label: 'Open' },
  { value: 'in-progress', label: 'In Progress' },
  { value: 'awaiting-user', label: 'Awaiting User' },
  { value: 'resolved', label: 'Resolved' },
  { value: 'closed', label: 'Closed' }
];

const URGENCY_OPTIONS = [
  { value: '', label: 'All Urgency' },
  { value: 'low', label: 'Low' },
  { value: 'medium', label: 'Medium' },
  { value: 'high', label: 'High' },
  { value: 'critical', label: 'Critical' }
];

const ISSUE_TYPE_OPTIONS = [
  { value: '', label: 'All Types' },
  { value: 'hardware', label: 'Hardware' },
  { value: 'software', label: 'Software' },
  { value: 'network', label: 'Network' },
  { value: 'account', label: 'Account' },
  { value: 'security', label: 'Security' },
  { value: 'other', label: 'Other' }
];

const SORT_OPTIONS = [
  { value: 'createdAt:desc', label: 'Newest First' },
  { value: 'createdAt:asc', label: 'Oldest First' },
  { value: 'updatedAt:desc', label: 'Recently Updated' },
  { value: 'urgency:desc', label: 'High Urgency First' },
  { value: 'title:asc', label: 'Title A-Z' }
];

const BULK_ACTIONS = [
  { value: '', label: 'Bulk Actions', disabled: true },
  { value: 'in-progress', label: 'Mark In Progress' },
  { value: 'resolved', label: 'Mark Resolved' },
  { value: 'closed', label: 'Mark Closed' },
  { value: 'reopen', label: 'Reopen Ticket' }
];

/**
 * Tickets Component - Enterprise-grade ticket management interface
 * Features: Pagination, Filtering, Bulk Operations, Export, Real-time updates
 */
const Tickets = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const abortControllerRef = useRef(null);
  
  // State management
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedTickets, setSelectedTickets] = useState(new Set());
  const [bulkAction, setBulkAction] = useState('');
  const [exporting, setExporting] = useState(false);
  const [processingBulk, setProcessingBulk] = useState(false);
  
  const [filters, setFilters] = useState({
    status: '',
    urgency: '',
    issueType: '',
    assignedTo: '',
    dateFrom: '',
    dateTo: '',
    search: '',
    page: 1,
    limit: TICKET_CONFIG.PAGE_SIZE,
    sortBy: 'createdAt',
    sortOrder: 'desc'
  });
  
  const [pagination, setPagination] = useState({
    totalPages: 1,
    currentPage: 1,
    total: 0,
    hasNext: false,
    hasPrev: false
  });

  // Initialize and cleanup
  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }
    
    fetchTickets();
    
    // Cleanup function
    return () => {
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }
    };
  }, [isAuthenticated, navigate]);

  // Fetch tickets with proper error handling and cancellation
  const fetchTickets = useCallback(async () => {
    // Cancel previous request if exists
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }
    
    // Create new abort controller
    abortControllerRef.current = new AbortController();
    
    try {
      setLoading(true);
      setError(null);
      
      // Validate filters before making request
      const validationErrors = validateFilters(filters);
      if (validationErrors.length > 0) {
        throw new Error(`Invalid filters: ${validationErrors.join(', ')}`);
      }
      
      const response = await ticketService.getTickets(filters);
      
      // Handle API response
      if (response?.success) {
        const ticketsData = extractTicketsFromResponse(response);
        const paginationData = extractPaginationFromResponse(response);
        
        setTickets(ticketsData);
        setPagination(paginationData);
        
        // Reset selection if tickets changed significantly
        if (selectedTickets.size > 0) {
          const remainingSelections = Array.from(selectedTickets).filter(id =>
            ticketsData.some(ticket => ticket._id === id)
          );
          setSelectedTickets(new Set(remainingSelections));
        }
      } else {
        throw new Error(response?.message || 'Failed to load tickets');
      }
    } catch (error) {
      // Ignore abort errors
      if (error.name === 'AbortError') {
        console.log('Request aborted');
        return;
      }
      
      console.error('Error fetching tickets:', error);
      setError({
        message: error.message || 'Failed to load tickets. Please try again.',
        type: 'fetch',
        retryable: true
      });
      setTickets([]);
    } finally {
      setLoading(false);
    }
  }, [filters, selectedTickets]);

  // Response data extraction utilities
  const extractTicketsFromResponse = (response) => {
    // Multiple possible response structures
    const ticketsData = 
      response.data?.tickets ||
      response.tickets ||
      response.data?.data?.tickets ||
      response.data ||
      [];
    
    // Validate and ensure array
    if (!Array.isArray(ticketsData)) {
      console.warn('Expected array for tickets, got:', typeof ticketsData);
      return [];
    }
    
    // Filter out invalid tickets
    return ticketsData.filter(ticket => 
      ticket && 
      (ticket._id || ticket.id) && 
      typeof ticket === 'object'
    );
  };

  const extractPaginationFromResponse = (response) => {
    const total = response.total || response.data?.total || 0;
    const currentPage = response.currentPage || response.data?.currentPage || filters.page;
    const totalPages = response.totalPages || response.data?.totalPages || 1;
    
    return {
      total,
      currentPage,
      totalPages,
      hasNext: currentPage < totalPages,
      hasPrev: currentPage > 1
    };
  };

  // Debounced search handler
  const handleSearch = useCallback(
    debounce((value) => {
      setFilters(prev => ({
        ...prev,
        search: value.trim(),
        page: 1
      }));
    }, TICKET_CONFIG.DEBOUNCE_DELAY),
    []
  );

  // Filter handlers
  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: key === 'search' ? prev.page : 1 // Only keep page for search
    }));
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= pagination.totalPages) {
      setFilters(prev => ({ ...prev, page: newPage }));
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  const clearFilters = () => {
    setFilters({
      status: '',
      urgency: '',
      issueType: '',
      assignedTo: '',
      dateFrom: '',
      dateTo: '',
      search: '',
      page: 1,
      limit: TICKET_CONFIG.PAGE_SIZE,
      sortBy: 'createdAt',
      sortOrder: 'desc'
    });
    setSelectedTickets(new Set());
  };

  // Bulk operations
  const handleBulkAction = async () => {
    if (!bulkAction || selectedTickets.size === 0) return;
    
    if (selectedTickets.size > TICKET_CONFIG.MAX_SELECTION) {
      setError({
        message: `Cannot process more than ${TICKET_CONFIG.MAX_SELECTION} tickets at once`,
        type: 'bulk',
        retryable: false
      });
      return;
    }
    
    try {
      setProcessingBulk(true);
      
      await ticketService.bulkUpdateStatus(
        Array.from(selectedTickets), 
        bulkAction,
        `Bulk action performed by ${user?.email || 'system'}`
      );
      
      // Refresh data
      await fetchTickets();
      
      // Reset selections
      setSelectedTickets(new Set());
      setBulkAction('');
      
      // Show success feedback (consider adding toast notification)
      console.log(`Successfully updated ${selectedTickets.size} tickets`);
      
    } catch (error) {
      console.error('Bulk update error:', error);
      setError({
        message: 'Bulk update failed. Please try again or contact support.',
        type: 'bulk',
        retryable: true
      });
    } finally {
      setProcessingBulk(false);
    }
  };

  // Selection management
  const handleTicketSelect = (ticketId) => {
    if (!ticketId) return;
    
    setSelectedTickets(prev => {
      const newSelection = new Set(prev);
      if (newSelection.has(ticketId)) {
        newSelection.delete(ticketId);
      } else if (newSelection.size < TICKET_CONFIG.MAX_SELECTION) {
        newSelection.add(ticketId);
      } else {
        setError({
          message: `Maximum selection limit is ${TICKET_CONFIG.MAX_SELECTION} tickets`,
          type: 'selection',
          retryable: false
        });
      }
      return newSelection;
    });
  };

  const selectAllTickets = () => {
    if (tickets.length === 0) return;
    
    const validTicketIds = tickets
      .map(ticket => ticket._id)
      .filter(id => id)
      .slice(0, TICKET_CONFIG.MAX_SELECTION);
    
    if (selectedTickets.size === validTicketIds.length) {
      setSelectedTickets(new Set());
    } else {
      setSelectedTickets(new Set(validTicketIds));
    }
  };

  // Export functionality
  const exportTickets = async () => {
    try {
      setExporting(true);
      
      const blob = await ticketService.exportTickets(filters, TICKET_CONFIG.EXPORT_FORMAT);
      
      // Create download link
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
      
      a.href = url;
      a.download = `tickets-export-${timestamp}.${TICKET_CONFIG.EXPORT_FORMAT}`;
      a.style.display = 'none';
      
      document.body.appendChild(a);
      a.click();
      
      // Cleanup
      setTimeout(() => {
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      }, 100);
      
    } catch (error) {
      console.error('Export error:', error);
      setError({
        message: 'Export failed. Please try again or contact support.',
        type: 'export',
        retryable: true
      });
    } finally {
      setExporting(false);
    }
  };

  // Navigation
  const navigateToCreateTicket = () => {
    navigate('/tickets/create');
  };

  const navigateToTicketDetail = (ticketId) => {
    if (ticketId) {
      navigate(`/tickets/${ticketId}`);
    }
  };

  // Error handling
  const clearError = () => {
    setError(null);
  };

  const retryAction = () => {
    if (error?.retryable) {
      clearError();
      fetchTickets();
    }
  };

  // Render loading state
  if (loading && tickets.length === 0) {
    return (
      <>
        <Navbar />
        <div className="page-container">
          <LoadingSpinner 
            message="Loading tickets..." 
            fullPage={true}
          />
        </div>
      </>
    );
  }

  return (
    <ErrorBoundary>
      <>
        <Navbar />
        <div className="tickets-page">
          {/* Header Section */}
          <header className="tickets-header">
            <div className="header-content">
              <h1 className="page-title">
                {user?.role === 'staff' ? 'My Support Tickets' : 'Ticket Management'}
              </h1>
              <p className="page-subtitle">
                Manage and track all support requests in one place
              </p>
            </div>
            
            <div className="header-actions">
              {user?.role === 'staff' && (
                <button
                  onClick={navigateToCreateTicket}
                  className="btn btn-primary btn-icon"
                  aria-label="Create new ticket"
                >
                  <span className="icon">+</span>
                  Create Ticket
                </button>
              )}
              
              <button
                onClick={exportTickets}
                className="btn btn-secondary"
                disabled={exporting || tickets.length === 0}
                aria-label="Export tickets"
              >
                {exporting ? 'Exporting...' : 'Export CSV'}
              </button>
            </div>
          </header>

          {/* Error Display */}
          {error && (
            <div className={`alert alert-${error.type}`}>
              <div className="alert-content">
                <strong>Error:</strong> {error.message}
              </div>
              <div className="alert-actions">
                {error.retryable && (
                  <button onClick={retryAction} className="btn btn-sm">
                    Retry
                  </button>
                )}
                <button onClick={clearError} className="btn btn-sm btn-text">
                  Dismiss
                </button>
              </div>
            </div>
          )}

          {/* Filters Section */}
          <section className="filters-section" aria-label="Ticket filters">
            <div className="search-container">
              <div className="search-input-wrapper">
                <input
                  type="text"
                  className="search-input"
                  placeholder="Search tickets..."
                  defaultValue={filters.search}
                  onChange={(e) => handleSearch(e.target.value)}
                  aria-label="Search tickets"
                />
                <span className="search-icon"></span>
              </div>
            </div>

            <div className="filters-grid">
              <div className="filter-group">
                <label htmlFor="status-filter" className="filter-label">
                  Status
                </label>
                <select
                  id="status-filter"
                  className="filter-select"
                  value={filters.status}
                  onChange={(e) => handleFilterChange('status', e.target.value)}
                  aria-label="Filter by status"
                >
                  {STATUS_OPTIONS.map(option => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
              </div>

              <div className="filter-group">
                <label htmlFor="urgency-filter" className="filter-label">
                  Urgency
                </label>
                <select
                  id="urgency-filter"
                  className="filter-select"
                  value={filters.urgency}
                  onChange={(e) => handleFilterChange('urgency', e.target.value)}
                  aria-label="Filter by urgency"
                >
                  {URGENCY_OPTIONS.map(option => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
              </div>

              <div className="filter-group">
                <label htmlFor="type-filter" className="filter-label">
                  Type
                </label>
                <select
                  id="type-filter"
                  className="filter-select"
                  value={filters.issueType}
                  onChange={(e) => handleFilterChange('issueType', e.target.value)}
                  aria-label="Filter by issue type"
                >
                  {ISSUE_TYPE_OPTIONS.map(option => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
              </div>

              <div className="filter-group">
                <label htmlFor="date-from" className="filter-label">
                  From Date
                </label>
                <input
                  id="date-from"
                  type="date"
                  className="filter-input"
                  value={filters.dateFrom}
                  onChange={(e) => handleFilterChange('dateFrom', e.target.value)}
                  aria-label="Filter from date"
                />
              </div>

              <div className="filter-group">
                <label htmlFor="date-to" className="filter-label">
                  To Date
                </label>
                <input
                  id="date-to"
                  type="date"
                  className="filter-input"
                  value={filters.dateTo}
                  onChange={(e) => handleFilterChange('dateTo', e.target.value)}
                  aria-label="Filter to date"
                  min={filters.dateFrom}
                />
              </div>

              <div className="filter-group">
                <label htmlFor="sort-filter" className="filter-label">
                  Sort By
                </label>
                <select
                  id="sort-filter"
                  className="filter-select"
                  value={`${filters.sortBy}:${filters.sortOrder}`}
                  onChange={(e) => {
                    const [sortBy, sortOrder] = e.target.value.split(':');
                    handleFilterChange('sortBy', sortBy);
                    handleFilterChange('sortOrder', sortOrder);
                  }}
                  aria-label="Sort tickets"
                >
                  {SORT_OPTIONS.map(option => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
              </div>

              <div className="filter-group">
                <button
                  onClick={clearFilters}
                  className="btn btn-secondary btn-block"
                  aria-label="Clear all filters"
                >
                  Clear Filters
                </button>
              </div>
            </div>
          </section>

          {/* Bulk Actions */}
          {selectedTickets.size > 0 && (
            <section className="bulk-actions-section" aria-label="Bulk actions">
              <div className="bulk-header">
                <span className="bulk-count">
                  {selectedTickets.size} ticket(s) selected
                </span>
                <div className="bulk-controls">
                  <select
                    className="bulk-select"
                    value={bulkAction}
                    onChange={(e) => setBulkAction(e.target.value)}
                    disabled={processingBulk}
                    aria-label="Select bulk action"
                  >
                    {BULK_ACTIONS.map(action => (
                      <option 
                        key={action.value} 
                        value={action.value}
                        disabled={action.disabled}
                      >
                        {action.label}
                      </option>
                    ))}
                  </select>
                  
                  <button
                    onClick={handleBulkAction}
                    className="btn btn-primary"
                    disabled={!bulkAction || processingBulk}
                    aria-label="Apply bulk action"
                  >
                    {processingBulk ? 'Processing...' : 'Apply'}
                  </button>
                  
                  <button
                    onClick={() => setSelectedTickets(new Set())}
                    className="btn btn-text"
                    aria-label="Clear selection"
                  >
                    Clear Selection
                  </button>
                </div>
              </div>
            </section>
          )}

          {/* Main Content */}
          <main className="tickets-container" aria-label="Ticket list">
            {tickets.length === 0 ? (
              <div className="empty-state" aria-label="No tickets">
                <div className="empty-icon"></div>
                <h3 className="empty-title">No Tickets Found</h3>
                <p className="empty-message">
                  {filters.search || filters.status || filters.dateFrom
                    ? 'No tickets match your current filters. Try adjusting your search criteria.'
                    : "You haven't created any tickets yet."}
                </p>
                {user?.role === 'staff' && (
                  <button
                    onClick={navigateToCreateTicket}
                    className="btn btn-primary"
                  >
                    Create Your First Ticket
                  </button>
                )}
              </div>
            ) : (
              <>
                {/* Stats and Controls */}
                <div className="tickets-stats">
                  <div className="stats-info">
                    <span className="stats-text">
                      Showing {tickets.length} of {pagination.total} tickets
                    </span>
                    <span className="stats-subtext">
                      Page {pagination.currentPage} of {pagination.totalPages}
                    </span>
                  </div>
                  
                  <div className="stats-controls">
                    <label className="checkbox-label">
                      <input
                        type="checkbox"
                        checked={selectedTickets.size === tickets.length && tickets.length > 0}
                        onChange={selectAllTickets}
                        disabled={tickets.length === 0}
                        aria-label="Select all tickets"
                      />
                      <span>Select All</span>
                    </label>
                  </div>
                </div>

                {/* Tickets Grid */}
                <div className="tickets-grid" role="list" aria-label="Tickets">
                  {tickets.map(ticket => (
                    <div 
                      key={ticket._id} 
                      className="ticket-card-wrapper"
                      role="listitem"
                      aria-label={`Ticket: ${ticket.title}`}
                    >
                      <input
                        type="checkbox"
                        className="ticket-select-checkbox"
                        checked={selectedTickets.has(ticket._id)}
                        onChange={() => handleTicketSelect(ticket._id)}
                        aria-label={`Select ticket: ${ticket.title}`}
                      />
                      
                      <div 
                        className="ticket-card-content"
                        onClick={() => navigateToTicketDetail(ticket._id)}
                        role="button"
                        tabIndex={0}
                        onKeyPress={(e) => {
                          if (e.key === 'Enter' || e.key === ' ') {
                            navigateToTicketDetail(ticket._id);
                          }
                        }}
                        aria-label={`View details for ticket: ${ticket.title}`}
                      >
                        <TicketCard ticket={ticket} />
                      </div>
                    </div>
                  ))}
                </div>

                {/* Pagination */}
                {pagination.totalPages > 1 && (
                  <nav className="pagination" aria-label="Ticket pagination">
                    <button
                      onClick={() => handlePageChange(pagination.currentPage - 1)}
                      disabled={!pagination.hasPrev || loading}
                      className="pagination-btn"
                      aria-label="Previous page"
                    >
                       Previous
                    </button>
                    
                    <div className="pagination-info">
                      <span className="pagination-text">
                        Page {pagination.currentPage} of {pagination.totalPages}
                      </span>
                      <span className="pagination-total">
                        ({pagination.total} total tickets)
                      </span>
                    </div>
                    
                    <button
                      onClick={() => handlePageChange(pagination.currentPage + 1)}
                      disabled={!pagination.hasNext || loading}
                      className="pagination-btn"
                      aria-label="Next page"
                    >
                      Next 
                    </button>
                  </nav>
                )}
              </>
            )}
          </main>

          {/* Loading overlay for subsequent loads */}
          {loading && tickets.length > 0 && (
            <div className="loading-overlay">
              <LoadingSpinner message="Updating tickets..." />
            </div>
          )}
        </div>
      </>
    </ErrorBoundary>
  );
};

export default Tickets;
